insert into unit_enemy_data values (314001, '擅长的作业', 314001, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '首先完成擅长的作业吧。\\n不能一直在这里停滞不前。暑假作业\\n还剩下了很多很多哦！\\n\\n·战斗开始后，会掉落大量的作业，\\n\u3000对敌方全体造成物理伤害（中）。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n·战斗中每经过一定时间，怪物就会\\n\u3000逼近。');
insert into unit_enemy_data values (314002, '有些棘手的作业', 314002, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '每个人都有不擅长科目的作业。不自觉地往后拖，\\n积压到最后就会变成大麻烦。\\n我们不能止步于此！\\n\\n·战斗开始后，会掉落大量的作业，\\n\u3000对敌方全体造成物理伤害（中）。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n·战斗中每经过一定时间，怪物就会\\n\u3000逼近。');
insert into unit_enemy_data values (314003, '非常棘手的作业', 314003, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '光看着就头晕脑胀，让人发出哀嚎的作业。\\n按捺住扔下笔跑出去的冲动，\\n踏踏实实前进才是攻略的关键！\\n\\n·战斗开始后，会掉落大量的作业，\\n\u3000对敌方全体造成物理伤害（中）。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。\\n·战斗中每经过一定时间，怪物就会\\n\u3000逼近。');
