insert into unit_enemy_data values (314006, '留到暑假最后一天的作业', 314006, 0, 16, 270, 3000, 1, 1.0, 0, 0, 0, '在暑假期间一直逃避的最后一项作业。\\n拖延至今的悔恨一下子袭来。\\n能够全部完成并带着笑容迎接开学吗！？\\n\\n·战斗开始后，会掉落大量的作业，\\n\u3000对敌方全体造成物理伤害（中）。\\n·战斗中每经过一定时间，怪物就会\\n\u3000逼近。');
