insert into unit_enemy_data values (314101, '歌之女王水母', 314100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】像花一样在海里摇曳生姿，奏响歌声的\\n歌之水母女王。在经过装扮的美丽外表下，\\n隐藏着带有剧毒的体液。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。会根据最大生命值，\\n\u3000对处于猛毒状态下的敌人\\n\u3000造成一定比例的魔法伤害。同时对敌方全体赋予中毒状态，\\n\u3000对已经处于中毒状态的敌人赋予猛毒状态。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n·对敌方全体造成魔法伤害（小），\\n\u3000并赋予中毒状态。若敌人处于猛毒状态，使其眩晕。\\n·歌之女王水母造成的中毒和猛毒伤害\\n\u3000不会使角色的技能值回复。');
insert into unit_enemy_data values (314102, '歌之女王水母', 314100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】像花一样在海里摇曳生姿，奏响歌声的\\n歌之水母女王。在经过装扮的美丽外表下，\\n隐藏着带有剧毒的体液。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。会根据最大生命值，\\n\u3000对处于猛毒状态下的敌人\\n\u3000造成一定比例的魔法伤害。同时对敌方全体赋予中毒状态，\\n\u3000对已经处于中毒状态的敌人赋予猛毒状态。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n·对敌方全体造成魔法伤害（小），\\n\u3000并赋予中毒状态。若敌人处于猛毒状态，使其眩晕。\\n·歌之女王水母造成的中毒和猛毒伤害\\n\u3000不会使角色的技能值回复。');
insert into unit_enemy_data values (314103, '歌之女王水母', 314100, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】像花一样在海里摇曳生姿，奏响歌声的\\n歌之水母女王。在经过装扮的美丽外表下，\\n隐藏着带有剧毒的体液。\\n\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成魔法比例伤害。会根据最大生命值，\\n\u3000对处于猛毒状态下的敌人\\n\u3000造成一定比例的魔法伤害。同时对敌方全体赋予中毒状态，\\n\u3000对已经处于中毒状态的敌人赋予猛毒状态。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n·对敌方全体造成魔法伤害（小），\\n\u3000并赋予中毒状态。若敌人处于猛毒状态，使其眩晕。\\n·歌之女王水母造成的中毒和猛毒伤害\\n\u3000不会使角色的技能值回复。\\n·剩余时间少于25秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
insert into unit_enemy_data values (314104, '歌之女王水母', 314104, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】无数魔物袭来。其歌声轰然回响，\\n给舞台带来威胁。为了守护粉丝们的笑容，\\n少女们载歌载舞，坚持战斗。\\n\\n·战斗开始时，召唤3只歌之水母，\\n\u3000并将敌方全体击退。\\n·每打倒1只歌之水母，\\n\u3000歌之水母的剩余数就减1。\\n\u3000之后再次召唤歌之水母，\\n\u3000并击退队伍全体角色。\\n·歌之水母对剩余生命值最高的一名角色造成\\n\u3000魔法伤害（中），并赋予中毒状态。\\n·必杀技能会根据角色的生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000提升对处于猛毒状态的敌人造成的伤害。\\n\u3000同时，对敌人赋予中毒状态，对已经处于中毒\\n\u3000或猛毒状态的敌人赋予猛毒状态。\\n·对前方一名敌人造成魔法伤害（大）。\\n·歌之女王水母与歌之水母所造成的\\n\u3000中毒和猛毒伤害\\n\u3000不会使角色的技能值回复。');
insert into unit_enemy_data values (314105, '歌之女王水母', 314105, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】出现在面前的\\n是过去的我们。成长之后，少女们跨越过去，\\n让歌声承载那每一个瞬间的光辉。\\n\\n·召唤慈乐之音的暗影。\\n·对前方一名敌人造成魔法伤害（大）。');
insert into unit_enemy_data values (314106, '歌之女王水母', 314104, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】优雅之花乱舞，释放内心的\\n魔性。以剧毒的雨音为旋律，\\n歌姬唱起最后的歌曲。\\n\\n·必杀技能会根据角色的生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000提升对处于猛毒状态的敌人造成的伤害。\\n\u3000同时，对敌人赋予中毒状态，对已经处于中毒\\n\u3000或猛毒状态的敌人赋予猛毒状态。\\n·对前方一名敌人造成魔法伤害（大）。\\n·对敌方全体造成魔法伤害（小），\\n\u3000并赋予中毒状态。\\n\u3000若敌人处于猛毒状态，使其眩晕。\\n·歌之女王水母造成的中毒和猛毒伤害\\n\u3000不会使角色的技能值回复。\\n·剩余时间少于25秒时，会展开更为激烈凶暴的\\n\u3000攻击。');
