insert into unit_enemy_data values (314201, '诡笑邪医', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】展现卑劣本性的大江户怪医。\\n打算用研究得到的究极肉体\\n向世界复仇。\\n\\n·必杀技能会根据诡笑邪医的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，并使其陷入\\n\u3000中毒状态。\\n·召唤恶魔之瓶，击退敌方全体。\\n·恶魔之瓶经过一定时间后会中幅提升\\n\u3000诡笑邪医的物理攻击力、特大幅度\\n\u3000回复其技能值，随后消亡。\\n\u3000但是，若恶魔之瓶的生命值降至一定值以下，\\n\u3000则特大幅提升敌方全体的物理攻击力\\n\u3000魔法攻击力，\\n\u3000特大幅度回复其技能值，随后消亡。\\n\u3000此外，诡笑邪医下一次召唤的恶魔之瓶\\n\u3000将得到1阶段强化。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成物理伤害（小），\\n\u3000并中幅降低其行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (314202, '诡笑邪医', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】展现卑劣本性的大江户怪医。\\n打算用研究得到的究极肉体\\n向世界复仇。\\n\\n·必杀技能会根据诡笑邪医的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，并使其陷入\\n\u3000中毒状态。\\n·召唤恶魔之瓶，击退敌方全体。\\n·恶魔之瓶经过一定时间后会中幅提升\\n\u3000诡笑邪医的物理攻击力、特大幅度\\n\u3000回复其技能值，随后消亡。\\n\u3000但是，若恶魔之瓶的生命值降至一定值以下，\\n\u3000则特大幅提升敌方全体的物理攻击力\\n\u3000魔法攻击力，\\n\u3000特大幅度回复其技能值，随后消亡。\\n\u3000此外，诡笑邪医下一次召唤的恶魔之瓶\\n\u3000将得到1阶段强化。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成物理伤害（小），\\n\u3000并中幅降低其行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (314203, '诡笑邪医', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】展现卑劣本性的大江户怪医。\\n打算用研究得到的究极肉体\\n向世界复仇。\\n\\n·必杀技能会根据诡笑邪医的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，并使其陷入\\n\u3000中毒状态。\\n·召唤恶魔之瓶，击退敌方全体。\\n·恶魔之瓶经过一定时间后会特大幅度提升\\n\u3000诡笑邪医的物理攻击力、特大幅度\\n\u3000回复其技能值，随后消亡。\\n\u3000但是，若恶魔之瓶的生命值降至一定值以下，\\n\u3000则特大幅度提升敌方全体的物理攻击力\\n\u3000和魔法攻击力，\\n\u3000特大幅度回复其技能值，随后消亡。\\n\u3000此外，诡笑邪医下一次召唤的恶魔之瓶\\n\u3000将得到1阶段强化。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成物理伤害（小），\\n\u3000并中幅降低其行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·诡笑邪医自身的生命值低于30%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (314204, '诡笑邪医', 314204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】在幻药之下显现的是自己的幻象。\\n用至诚的医道，打破挡在面前的\\n邪恶实体吧。\\n\\n·召唤深月和雪的暗影与魔物。\\n·对前方一名敌人造成物理伤害（中）。');
insert into unit_enemy_data values (314205, '诡笑邪医', 314205, 0, 16, 270, 3000, 1, 0.0, 0, 0, 0, '【模式2】魔腕绽放的妖光洒向地面。被其魔性所吞噬的实验体们\\n迷失了自我而变得狂暴。\\n\\n·召唤魔物，击退敌方全体。\\n·每打倒1只被召唤的魔物，\\n\u3000目标剩余数就减少1。\\n\u3000之后再次召唤魔物，并击退敌方全体。\\n·特大幅度提升诡笑邪医以外\\n\u3000我方全体的物理防御力和魔法防御力。\\n\u3000在战斗开始后，当诡笑邪医的生命值\\n\u3000减少2000以上和4000以上时，\\n\u3000诡笑邪医的外形发生改变，\\n\u3000该技能效果会下降。');
insert into unit_enemy_data values (314206, '诡笑邪医', 314200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】肉体的进化升华到了极限。\\n进化到达终点，野心膨胀到极致，怪医的激愤在\\n轰鸣，要用这只魔腕掌控全世界。\\n\\n·必杀技能会根据诡笑邪医的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，并使其陷入\\n\u3000中毒状态。\\n·召唤恶魔之瓶，击退敌方全体。\\n·恶魔之瓶经过一定时间后会特大幅度提升\\n\u3000诡笑邪医的物理攻击力、物理防御力\\n\u3000和魔法防御力，特大幅度\\n\u3000回复其技能值，随后消亡。\\n\u3000但是，若恶魔之瓶的生命值降至一定值以下，\\n\u3000则中幅提升队伍全体的物理攻击力\\n\u3000和魔法攻击力直到战斗结束，\\n\u3000特大幅度回复其技能值，随后消亡。\\n\u3000此外，诡笑邪医下一次召唤的恶魔之瓶\\n\u3000将得到1阶段强化。\\n·对攻击力（物理攻击力或魔法攻击力）最高的\\n\u3000一名敌人造成物理伤害（特大），\\n\u3000并中幅降低其行动速度。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·诡笑邪医自身的生命值低于30%时，\\n\u3000会展开更为激烈凶暴的攻击。');
