insert into unit_enemy_data values (314301, '哀暴的小棉花糖', 314301, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】孤独无依还被当成赚钱工具，悲哀的情绪\\n失控后释放出真正力量的魔犬。\\n不摧毁一切，其失控不会停止。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成物理伤害（大）。同时小幅提升\\n\u3000自身的物理攻击力。\\n·对前方一名敌人造成物理伤害（小）。\\n·大幅降低最近三名角色的物理/魔法攻击力，\\n\u3000并大幅降低其物理防御力直到战斗结束。\\n·小幅提升自身的物理攻击力。');
insert into unit_enemy_data values (314302, '哀暴的小棉花糖', 314301, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】孤独无依还被当成赚钱工具，悲哀的情绪\\n失控后释放出真正力量的魔犬。\\n不摧毁一切，其失控不会停止。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成物理伤害（大）。同时小幅提升\\n\u3000自身的物理攻击力。\\n·对前方一名敌人造成物理伤害（小）。\\n·大幅降低最近三名角色的物理/魔法攻击力，\\n\u3000并大幅降低其物理防御力直到战斗结束。\\n·小幅提升自身的物理攻击力。');
insert into unit_enemy_data values (314303, '哀暴的小棉花糖', 314300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】孤独无依还被当成赚钱工具，悲哀的情绪\\n失控后释放出真正力量的魔犬。\\n不摧毁一切，其失控不会停止。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成物理伤害（中）。同时小幅提升\\n\u3000自身的物理攻击力。此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·对前方一名敌人造成物理伤害（中）。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·大幅降低最近三名角色的物理/魔法攻击力，\\n\u3000并大幅降低其物理防御力直到战斗结束。\\n·中幅提升自身的物理攻击力。\\n·剩余时间少于30秒时，会大幅降低敌方全体的物理防御力，\\n\u3000大幅提升自身的物理/魔法防御力，\\n\u3000并展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (314304, '哀暴的小棉花糖', 314304, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】从异界误入此地的灵体如镜子一般\\n变成众人的模样，挡住前路。万圣节救援队是否\\n能够顺利取胜、拯救世界呢？\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成物理伤害（大）。同时小幅提升\\n\u3000自身的物理攻击力。此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·对前方一名敌人造成物理伤害（中）。');
insert into unit_enemy_data values (314305, '哀暴的小棉花糖', 314305, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】从失控状态突然缩成一团变成球体，\\n隔断一切攻击。就算是拼尽全力的一击，\\n在它的这般模样面前也不过是挠痒痒吧。\\n\\n·战斗开始时，极大幅度提升自身的物理/\\n\u3000魔法防御力。\\n·在战斗中，哀暴的小棉花糖不会行动。');
insert into unit_enemy_data values (314306, '哀暴的小棉花糖', 314300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】作为超特级妖魔，将灵力解放到极限，\\n以摧毁世界平衡之势嬉闹起来的\\n小棉花糖。它所有的热情能得到包容吗？\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对其造成物理伤害（中）。同时小幅提升\\n\u3000自身的物理攻击力。此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·对前方一名敌人造成物理伤害（中）。\\n\u3000此攻击容易受到物理攻击力\\n\u3000提升或下降效果的影响。\\n·大幅降低最近三名角色的物理/魔法攻击力，\\n\u3000并中幅降低其物理防御力直到战斗结束。\\n·小幅提升自身的物理攻击力。\\n·剩余时间少于30秒时，会中幅降低敌方全体的物理防御力，\\n\u3000中幅提升自身的物理/魔法防御力，\\n\u3000并展开更为激烈凶暴的攻击。');
