insert into unit_enemy_data values (314401, '三重反物质兽', 314400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】吸收了魔法少女们的力量而\\n获得进化的反物质兽。兰德索尔会就这样\\n落入魔掌吗！？永不言败三人组！\\n\\n·必杀技能会使离自身最近的三名敌人中，生命值\\n\u3000在80%以下的角色陷入魅惑状态。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对离自身最近的三名敌人造成一定比例的魔法伤害。\\n\u3000提升对处于魅惑状态的敌人造成的伤害。\\n·召唤反物质兽之仆，\\n\u3000击退敌方全体。反物质兽之仆经过一定时间后，\\n\u3000会使最近的一名敌人陷入魅惑状态，\\n\u3000随后消失。\\n\u3000但是，若反物质兽的生命值降至一定值以下，\\n\u3000则特大幅度提升队伍的物理攻击力\\n\u3000和魔法攻击力，随后消亡。\\n·对最接近自身的2名敌人造成\\n\u3000魔法伤害（小）。在此之上，对自身展开物理攻击无效化屏障。\\n·根据角色的最大生命值，\\n\u3000对最近两名角色造成一定比例的魔法伤害。');
insert into unit_enemy_data values (314402, '三重反物质兽', 314400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】吸收了魔法少女们的力量而\\n获得进化的反物质兽。兰德索尔会就这样\\n落入魔掌吗！？永不言败三人组！\\n\\n·必杀技能会使离自身最近的三名敌人中，生命值\\n\u3000在80%以下的角色陷入魅惑状态。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对离自身最近的三名敌人造成一定比例的魔法伤害。\\n\u3000提升对处于魅惑状态的敌人造成的伤害。\\n·召唤反物质兽之仆，\\n\u3000击退敌方全体。反物质兽之仆经过一定时间后，\\n\u3000会使最近的一名敌人陷入魅惑状态，\\n\u3000随后消失。\\n\u3000但是，若反物质兽的生命值降至一定值以下，\\n\u3000则特大幅度提升队伍的物理攻击力\\n\u3000和魔法攻击力，随后消亡。\\n·对最接近自身的2名敌人造成\\n\u3000魔法伤害（小）。在此之上，对自身展开物理攻击无效化屏障。\\n·根据角色的最大生命值，\\n\u3000对最近两名角色造成一定比例的魔法伤害。');
insert into unit_enemy_data values (314403, '三重反物质兽', 314400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】吸收了魔法少女们的力量而\\n获得进化的反物质兽。兰德索尔会就这样\\n落入魔掌吗！？永不言败三人组！\\n\\n·必杀技能会使离自身最近的三名敌人中，生命值\\n\u3000在80%以下的角色陷入魅惑状态。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对离自身最近的三名敌人造成一定比例的魔法伤害。\\n\u3000提升对处于魅惑状态的敌人造成的伤害。\\n·召唤反物质兽之仆，\\n\u3000击退敌方全体。反物质兽之仆经过一定时间后，\\n\u3000会使最近的一名敌人陷入魅惑状态，\\n\u3000随后消失。\\n\u3000但是，若反物质兽的生命值降至一定值以下，\\n\u3000则特大幅度提升队伍的物理攻击力\\n\u3000和魔法攻击力，随后消亡。\\n·对最接近自身的2名敌人造成\\n\u3000魔法伤害（小）。在此之上，对自身展开物理攻击无效化屏障。\\n·根据角色的最大生命值，\\n\u3000对最近两名角色造成一定比例的魔法伤害。\\n·三重反物质兽自身的生命值低于30%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (314404, '三重反物质兽', 314405, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】受困于黑暗的旋涡，即将失去希望的\\n魔法少女们——她们在这个时候听到的是\\n来自重要的人的鼓励。振作起来，魔法少女！\\n\\n·必杀技能会使前方三名角色中\\n\u3000生命值在75%以下的角色陷入魅惑状态。\\n\u3000随后，根据角色的最大生命值，对前方三名角色造成\\n\u3000魔法比例伤害。\\n\u3000提升对处于魅惑状态的敌人造成的伤害。\\n·战斗开始时，使敌方全体陷入诅咒状态。\\n\u3000该技能所造成的诅咒伤害\\n\u3000不会回复技能值。\\n\u3000此外，若想通过某种效果回复生命值，\\n\u3000将会减少原本回复量的60%。\\n·对前方两名角色造成魔法伤害（小）。');
insert into unit_enemy_data values (314405, '三重反物质兽', 314405, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】仇敌再次积蓄并使出黑暗力量。\\n但是，开始失控的并非只有敌方！？\u3000已经没有任何人能够阻止这三人了！\\n\\n·展开屏障，完全防御敌人的攻击。\\n\u3000屏障的耐久度与伤害量无关，\\n\u3000每次受到攻击都会逐渐减少耐久度，\\n\u3000受到490次攻击后，\\n\u3000屏障就会破裂。\\n·对前方两名角色造成魔法伤害（小）。');
insert into unit_enemy_data values (314406, '三重反物质兽', 314400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】战斗终于进入了高潮。\\n为了可爱的学生、弟弟、虐待狂先生，怀揣着\\n爱与希望与黑暗勇往直前吧，三人组！\\n\\n·必杀技能会使前方三名角色中，生命值\\n\u3000在80%以下的角色陷入魅惑状态。\\n\u3000随后，根据角色的最大生命值，对前方三名角色造成\\n\u3000魔法比例伤害。\\n\u3000提升对处于魅惑状态的敌人造成的伤害。\\n·召唤反物质兽之仆，\\n\u3000击退敌方全体。\\n\u3000反物质兽之仆经过一定时间后\\n\u3000会使前方一名敌人陷入魅惑状态，\\n\u3000随后消亡。\\n\u3000但是，若反物质兽之仆的生命值\\n\u3000降至一定值以下，则特大幅度提升队伍全体的物理攻击力和\\n\u3000魔法攻击力，随后消亡。\\n·对前方两名角色造成魔法伤害（小）。\\n\u3000同时为自身展开物理无效化屏障。\\n·根据角色的最大生命值，对前方两名角色造成\\n\u3000魔法比例伤害。\\n·三重反物质兽自身的生命值低于30%时，\\n\u3000会展开更为激烈凶暴的攻击。');
