insert into unit_enemy_data values (314503, '孤独的圣夜精灵', 314500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】很久以前就被人们遗忘的悲伤精灵。\\n失去了信仰，为了排遣寂寞，想用恶作剧来吸引\\n人们的关注。它能够镇住混乱的比赛会场，\\n再次找回信仰吗！？\\n\\n·必杀技能会根据孤独的圣夜精灵的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n·根据孤独的圣夜精灵的魔法攻击力\\n\u3000和角色的最大生命值，对前方三名角色造成\\n\u3000一定比例的魔法伤害，并将其大幅度击飞。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·极大幅度提升孤独的圣夜精灵自身的\\n\u3000物理/魔法防御力。\\n·孤独的圣夜精灵自身的生命值低于40%时，\\n\u3000大幅提升自身的物理/魔法防御力，\\n\u3000并展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (314504, '孤独的圣夜精灵', 314500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】精灵用点心做出来的宫子们施展甜蜜猛攻，\\n与精灵的恶作剧一起蜂拥而至。用圣夜里培养的\\n光辉灿烂的情谊，将冒牌货们击溃吧！\\n\\n·召唤宫子、真阳、璃乃的暗影。\\n·对前方三名角色造成魔法伤害（中），\\n\u3000并特大幅度降低其物理防御力。这个技能的效果\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (314505, '孤独的圣夜精灵', 314500, 0, 16, 270, 3000, 1, 0.0, 0, 0, 0, '【模式2】精灵从袋子里取出的是\\n充满诱惑的点心。只要有一瞬间的松懈，\\n就会掉入甜蜜的陷阱。用布丁力量打碎\\n甜蜜幻想吧！\\n\\n·必杀技能会根据孤独的圣夜精灵的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n·根据孤独的圣夜精灵的魔法攻击力\\n\u3000和角色的最大生命值，对前方三名角色造成\\n\u3000一定比例的魔法伤害，并将其大幅度击飞。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·根据角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害，当角色的生命值低于60%时，\\n\u3000使其陷入魅惑状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (314506, '孤独的圣夜精灵', 314500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】孤独精灵的哀号响彻圣夜。\\n劝解不顾一切的发狂精灵，用布丁的情谊将大家的\\n幸福圣诞节和精灵的笑容找回来吧！\\n\\n·必杀技能会根据孤独的圣夜精灵的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害。\\n·根据孤独的圣夜精灵的魔法攻击力\\n\u3000和角色的最大生命值，对前方三名角色造成\\n\u3000一定比例的魔法伤害，并将其大幅度击飞。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·极大幅度提升孤独的圣夜精灵自身的\\n\u3000物理/魔法防御力。\\n·孤独的圣夜精灵自身的生命值低于30%时，\\n\u3000会展开更为激烈凶暴的攻击。');
