insert into unit_enemy_data values (314601, '邪牛神哞怪', 314600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】吸取吉祥物品上的祈愿之力，狂暴的\\n牛之魔物。陷入愤怒的猛牛会击碎周围人们的\\n愿望。\\n\\n·必杀技能会根据邪牛神哞怪的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害，并使其陷入烧伤状态。\\n·对魔法攻击力最高的一名敌人\\n\u3000造成一定比例的物理伤害，将其击退的同时\\n\u3000使其眩晕。\\n·对前方第一名、第二名角色造成\\n\u3000物理伤害（大）。技能发动后，大幅提升自身的物理攻击力，\\n\u3000大幅降低物理/魔法防御力。');
insert into unit_enemy_data values (314602, '邪牛神哞怪', 314600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】吸取吉祥物品上的祈愿之力，狂暴的\\n牛之魔物。陷入愤怒的猛牛会击碎周围人们的\\n愿望。\\n\\n·必杀技能会根据邪牛神哞怪的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害，并使其陷入烧伤状态。\\n·对魔法攻击力最高的一名敌人\\n\u3000造成一定比例的物理伤害，将其击退的同时\\n\u3000使其眩晕。\\n·对前方第一名、第二名角色造成\\n\u3000物理伤害（大）。技能发动后，大幅提升自身的物理攻击力，\\n\u3000大幅降低物理/魔法防御力。');
insert into unit_enemy_data values (314603, '邪牛神哞怪', 314600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】吸取吉祥物品上的祈愿之力，狂暴的\\n牛之魔物。陷入愤怒的猛牛会击碎周围人们的\\n愿望。\\n\\n·必杀技能会根据邪牛神哞怪的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害，并使其陷入烧伤状态。\\n·普通攻击会根据邪牛神哞怪的物理攻击力\\n\u3000和角色的最大生命值，对前方一名敌人造成\\n\u3000一定比例的物理伤害。\\n·对魔法攻击力最高的一名敌人\\n\u3000造成一定比例的物理伤害，将其击退的同时\\n\u3000使其眩晕。\\n·对前方第一名、第二名角色造成\\n\u3000物理伤害（大）。技能发动后，大幅提升自身的物理攻击力，\\n\u3000大幅降低物理/魔法防御力。\\n·剩余时间少于30秒时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (314604, '邪牛神哞怪', 314600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】出于对力量的渴望，魔物率领手下，\\n再次踏上掠夺之路。打倒逼近的流氓，\\n守护年节菜的食材吧。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·邪牛神哞怪和上位兽人的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·战斗开始时，召唤3只上位兽人。\\n·每打倒1只上位兽人，上位兽人的\\n\u3000剩余数就减1。之后，再次召唤上位兽人。\\n·对前方一名敌人造成魔法伤害（小），\u3000大幅降低其物理防御力。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (314605, '邪牛神哞怪', 314605, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】猛牛的强韧肉体爆裂开来。\\n如果承受怪腕发出的全力殴打，肉体与其中的\\n所有烦恼都会烟消云散。\\n\\n·对来自敌人的攻击造成的麻痹、眩晕、束缚、冻结、\\n\u3000时间停止、行动速度降低效果\\n\u3000失去抗性。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·邪牛神哞怪的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据邪牛神哞怪的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000物理比例伤害，并使其陷入烧伤状态。\\n·普通攻击会根据邪牛神哞怪的物理攻击力\\n\u3000和角色的最大生命值，对前方一名敌人造成\\n\u3000物理比例伤害。\\n·对前方第一名、第二名角色造成\\n\u3000物理伤害（大）。技能发动后，大幅提升自身的物理攻击力，\\n\u3000大幅降低物理/魔法防御力。\\n·解除敌方全体的属性值提升效果，在一定时间内\\n\u3000展开战斗领域。\\n【战斗领域效果】\\n·将敌方全体的物理攻击力和魔法攻击力降低100%后，\\n\u3000极大幅度提升\\n\u3000物理攻击力和魔法攻击力。\\n·邪牛神哞怪的物理攻击力降低100%后，\\n\u3000极大幅度提升物理攻击力。');
insert into unit_enemy_data values (314606, '邪牛神哞怪', 314605, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】猛牛不顾身受重伤，嚣张地\\n挡住前路。打倒被执念所困的麻烦魔物，\\n夺回可喜可贺的新年吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·邪牛神哞怪的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据邪牛神哞怪的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000一定比例的物理伤害，并使其陷入烧伤状态。\\n·对魔法攻击力最高的一名敌人\\n\u3000造成一定比例的物理伤害，将其击退的同时\\n\u3000使其眩晕。\\n·对前方第一名、第二名角色造成\\n\u3000物理伤害（大）。技能发动后，大幅提升自身的物理攻击力，\\n\u3000大幅降低物理/魔法防御力。\\n·剩余时间少于30秒时，\\n\u3000会展开更为激烈凶暴的攻击。');
