insert into unit_enemy_data values (314701, '巨型魔像', 314700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】会接二连三呼唤魔像同族的\\n岩石魔物。撼动大地的巨腕\\n向冒险者们袭来。\\n\\n·由于主人公尚未醒来，在本次战斗中\\n\u3000所有角色的连结爆发\\n\u3000都无法发动。\\n·由于佩可莉姆尚未醒来，在本次战斗中佩可莉姆\\n\u3000（包含同名角色）不可编组。\\n·必杀技能会根据巨型魔像的物理攻击力和\\n\u3000角色的最大生命值，对前方四名角色造成\\n\u3000物理比例伤害。\\n·击退敌方全体，召唤魔像。并中幅提升自己的\\n\u3000物理攻击力。\\n·对前方第一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (314702, '巨型魔像', 314700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】会接二连三呼唤魔像同族的\\n岩石魔物。撼动大地的巨腕\\n向冒险者们袭来。\\n\\n·由于主人公尚未醒来，在本次战斗中\\n\u3000所有角色的连结爆发\\n\u3000都无法发动。\\n·由于佩可莉姆尚未醒来，在本次战斗中佩可莉姆\\n\u3000（包含同名角色）不可编组。\\n·必杀技能会根据巨型魔像的物理攻击力和\\n\u3000角色的最大生命值，对前方四名角色造成\\n\u3000物理比例伤害。\\n·击退敌方全体，召唤魔像。并中幅提升自己的\\n\u3000物理攻击力。\\n·对前方第一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (314703, '巨型魔像', 314700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】会接二连三呼唤魔像同族的\\n岩石魔物。撼动大地的巨腕\\n向冒险者们袭来。\\n\\n·由于主人公尚未醒来，在本次战斗中\\n\u3000所有角色的连结爆发\\n\u3000都无法发动。\\n·由于佩可莉姆尚未醒来，在本次战斗中佩可莉姆\\n\u3000（包含同名角色）不可编组。\\n·必杀技能会根据巨型魔像的物理攻击力和\\n\u3000角色的最大生命值，对前方四名角色造成\\n\u3000物理比例伤害。\\n·击退敌方全体，召唤魔像。并中幅提升自己的\\n\u3000物理攻击力。\\n·对前方第一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (314704, '巨型魔像', 314704, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】按照巨石的指示，其下属的魔像\\n挡在冒险者前方。岩石魔物们只知道\\n全力震动大地，一味地发起进攻。\\n\\n·由于主人公尚未醒来，在本次战斗中\\n\u3000所有角色的连结爆发\\n\u3000都无法发动。\\n·由于佩可莉姆尚未醒来，在本次战斗中佩可莉姆\\n\u3000（包含同名角色）不可编组。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会根据巨型魔像的物理攻击力和\\n\u3000角色的最大生命值，对前方四名角色造成\\n\u3000物理比例伤害。');
insert into unit_enemy_data values (314705, '巨型魔像', 314704, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】突然之间，众多幻象从\\n森林深处出现。在与同伴们相似的外表面前，\\n考验的是真正的情谊。\\n\\n·由于主人公尚未醒来，在本次战斗中\\n\u3000所有角色的连结爆发\\n\u3000都无法发动。\\n·由于佩可莉姆尚未醒来，在本次战斗中佩可莉姆\\n\u3000（包含同名角色）不可编组。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·击退敌方全体，召唤多个暗影。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (314706, '巨型魔像', 314706, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】伴随着满布裂纹的巨石发出的咆哮，\\n岩石们再次挡在前方。从岩石的躯体深处窥视一切的红眸\\n死死盯着敌人，用强壮的巨腕横扫一切。\\n\\n·由于主人公尚未醒来，在本次战斗中\\n\u3000所有角色的连结爆发\\n\u3000都无法发动。\\n·由于佩可莉姆尚未醒来，在本次战斗中佩可莉姆\\n\u3000（包含同名角色）不可编组。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会根据巨型魔像的物理攻击力和\\n\u3000角色的最大生命值，对前方四名角色造成\\n\u3000物理比例伤害。\\n·击退敌方全体，召唤魔像。并中幅提升自己的\\n\u3000物理攻击力。这个技能的效果每次发动时\\n\u3000都会提升。\\n·对前方第一名角色造成物理伤害（大）。');
