insert into unit_enemy_data values (314803, '超负荷之力', 314800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】从“对变强的渴望”诞生的力量。\\n什么才是真正的强大？\u3000不断自我提问，\\n就能找到答案吗？振作起来，\\n找回曾经的情谊吧。\\n\\n·必杀技能会对敌方全体造成物理伤害（大）。此外，若超负荷之力的\\n\u3000目标没有处于击破状态，\\n\u3000则提升造成的伤害。\\n·对敌方全体造成物理伤害（中），\\n\u3000并中幅降低其物理防御力。此外，若超负荷之力的\\n\u3000目标没有处于击破状态，\\n\u3000则提升造成的伤害及技能效果。这个\\n\u3000技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方第一名角色造成物理伤害（大）。\\n·超负荷之力的目标处于击破状态时，\\n\u3000中幅降低自身的物理防御力和\\n\u3000魔法防御力。');
insert into unit_enemy_data values (314804, '超负荷之力', 314804, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】从容器中溢出的超负荷之力所创造的\\n临时【美食殿堂】。依靠再次结下的这份情谊，\\n超越自我吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·超负荷之力的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·召唤佩可莉姆、可可萝、凯露和雪菲\\n\u3000的暗影。\\n·对前方第一名角色造成物理伤害（大）。\\n·超负荷之力的目标处于击破状态时，\\n\u3000中幅降低自身的物理防御力和\\n\u3000魔法防御力。');
insert into unit_enemy_data values (314805, '超负荷之力', 314805, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】溢出之力形成的球状物体，其脉动\\n发生变化，发起歼灭的强攻。思念之刃\\n能否突破剑雨，传达出去？\u3000超越全力，指明\\n前进的道路吧！\\n\\n·战斗开始时，超负荷之力启动，\\n\u3000解放本体更强的力量。\\n\u3000特大幅度降低敌方全体的生命值吸收，\\n\u3000展开更为激烈强力的攻击。\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·超负荷之力的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（特大）。\\n·对敌方全体造成物理伤害（中），\\n\u3000并小幅降低其物理防御力。此外，若超负荷之力光球的\\n\u3000目标没有处于击破状态，\\n\u3000则提升技能效果。这个技能的效果\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·超负荷之力的目标处于击破状态时，\\n\u3000大幅降低自身的物理防御力和\\n\u3000魔法防御力。');
insert into unit_enemy_data values (314806, '超负荷之力', 314804, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】溢出的另一股力量改变了形状，\\n化为苍蓝剑闪，如流星一般纷纷坠落。走过的路\\n与结下的情谊的证明。前往我所连结的未来！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·超负荷之力的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（大）。此外，若超负荷之力的\\n\u3000目标没有处于击破状态，\\n\u3000则提升造成的伤害。\\n·对敌方全体造成物理伤害（中），\\n\u3000并中幅降低其物理防御力。此外，若超负荷之力的\\n\u3000目标没有处于击破状态，\\n\u3000则提升造成的伤害及技能效果。这个\\n\u3000技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方第一名角色造成物理伤害（大）。\\n·超负荷之力的目标处于击破状态时，\\n\u3000中幅降低自身的物理防御力和\\n\u3000魔法防御力。');
insert into unit_enemy_data values (314901, '领主哥布林&哥布林手下', 314901, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式1】盘踞于沙漠一带的哥布林族\\n魁首。带着手下一起迎击来到城池的\\n老练冒险者。\\n\\n▼模式1突破条件\\n对领主哥布林&哥布林手下造成伤害，\\n使其生命值降到8880万以下。\\n\\n·战斗开始时，小幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，使用伤害上限技能，\\n\u3000当一次行动对其施加的伤害超过一定数值，\\n\u3000伤害量将根据施加的伤害值而减少。\\n·领主哥布林&哥布林手下的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n对敌方全体造成一定比例的物理伤害，\\n\u3000并将其大幅度击飞。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据角色的最大生命值，对敌方全体\\n\u3000造成一定比例的物理伤害。\\n·装甲车上的哥布林手下的目标\\n\u3000被击破时即会消失，并小幅降低\\n\u3000领主哥布林&哥布林手下的物理防御力和\\n\u3000魔法防御力。\\n·装甲车核心的目标被击破时，\\n\u3000小幅降低领主哥布林&哥布林手下的\\n\u3000物理防御力和魔法防御力。\\n·领主哥布林&哥布林手下的目标全部被击破时，\\n\u3000特大幅度降低物理防御力和魔法防御力，\\n\u3000并变更攻击行动。\\n·行动变更后，核心开始积蓄能量，\\n\u3000经过一定时间后，对自身赋予\\n\u3000持续回复技能值状态。积蓄能量期间，如果受到\\n\u3000一定量的伤害，则技能中断，\\n\u3000使自身陷入烧伤状态。\\n·若战斗超时，会使敌方全体陷入\\n\u3000无法战斗状态。');
insert into unit_enemy_data values (315001, '领主哥布林', 315001, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式2】见装甲车遭到破坏变成废物，魁首震怒。\\n派出哥布林族的强者，成为冒险者的下一道难关，\\n不会轻易让其突破。\\n\\n▼模式2突破条件\\n对巨型哥布林和专家哥布林造成伤害，\\n使其生命值降为0。\\n\\n·战斗开始时，对自身赋予无敌状态，\\n\u3000完全防御敌人的攻击。\\n·领主哥布林的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的剩余生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·若战斗超时，会使敌方全体陷入\\n\u3000无法战斗状态。');
insert into unit_enemy_data values (315002, '领主哥布林', 315002, 0, 16, 270, 3000, 1, 1.8, 0, 0, 0, '【模式3】统领沙漠的哥布林族魁首。\\n将属下被杀的怒气化为武器，以压倒性的力量\\n让眼前的一切化为尘土。\\n\\n▼模式3突破条件\\n对领主哥布林造成伤害，\\n使其生命值降为0。\\n\\n·战斗开始时，小幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·战斗开始时，使用伤害上限技能，\\n\u3000当一次行动对其施加的伤害超过一定数值，\\n\u3000伤害量将根据施加的伤害值而减少。\\n·领主哥布林的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的剩余生命值，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并大幅提升自身的物理攻击力。根据自身的物理攻击力，\\n\u3000增加所造成的伤害比例。\\n·对敌方全体造成物理伤害（大），\\n\u3000并使其眩晕。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方范围内的敌人造成物理伤害（大），\\n并将其大幅度击飞。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据角色的最大生命值，对前方一名角色造成\\n\u3000一定比例的物理伤害。\\n·剩余生命值在1872万以下时，特大幅度提升\\n\u3000物理攻击力和行动速度，\\n\u3000并展开更为激烈凶暴的攻击。\\n·若战斗超时，会使敌方全体陷入\\n\u3000无法战斗状态。');
