insert into unit_enemy_data values (315201, '仇鼠·空手干架的啾座', 315200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族可靠的少当家。曾经赤手空拳\\n打遍天下的威猛斗鼠，为了将家族从危机中救出，\\n不惜拿起武器投身抗争。\\n\\n·必杀技能会以前方第一名角色为中心，\\n\u3000对一定范围内的敌人造成物理伤害（大）。\\n·对前方第一名角色造成物理伤害（小），\\n\u3000中幅降低其物理防御力。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (315202, '仇鼠·空手干架的啾座', 315200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族可靠的少当家。曾经赤手空拳\\n打遍天下的威猛斗鼠，为了将家族从危机中救出，\\n不惜拿起武器投身抗争。\\n\\n·必杀技能会以前方第一名角色为中心，\\n\u3000对一定范围内的敌人造成物理伤害（大）。\\n·对前方第一名角色造成物理伤害（小），\\n\u3000中幅降低其物理防御力。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (315203, '仇鼠·空手干架的啾座', 315200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】老鼠家族可靠的少当家。曾经赤手空拳\\n打遍天下的威猛斗鼠，为了将家族从危机中救出，\\n不惜拿起武器投身抗争。\\n\\n·必杀技能会以前方第一名角色为中心，\\n\u3000对一定范围内的敌人造成物理伤害（大）。\\n·对前方第一名角色造成物理伤害（小），\\n\u3000中幅降低其物理防御力。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (315204, '仇鼠·空手干架的啾座', 315204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '老鼠家族首屈一指的武斗派。对再次全家族总动员抗敌这件事\\n感到热血沸腾，双眼因好战而炯炯发亮。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·仇鼠·空手干架的啾座的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (315205, '仇鼠·空手干架的啾座', 315204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '狂暴开膛手家族的特攻队长。总是站在前线，\\n用自己的背影引领年轻人。倾尽全力的一刀\\n挥出气势汹汹的殊死一击，能削掉对手的铠甲。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·仇鼠·空手干架的啾座的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以前方第一名角色为中心，\\n\u3000对一定范围内的敌人造成物理伤害（大）。\\n·对前方第一名角色造成物理伤害（小），\\n\u3000中幅降低其物理防御力。\\n·在战斗中，仇鼠·空手干架的啾座自身的剩余生命值\\n\u3000低于50%时，会特大幅度\\n\u3000降低自身的技能值，\\n\u3000并陷入昏迷，无法行动。并不再成为攻击的对象。\\n·在战斗中，仇鼠·空手干架的啾座自身的剩余生命值\\n\u3000低于50%时会陷入昏迷，\\n\u3000无法行动。\\n\u3000并不再成为攻击的对象。');
insert into unit_enemy_data values (315206, '仇鼠·空手干架的啾座', 315206, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '让其他族人又害怕又敬慕的家族大哥。抱着为家人失去生命\\n也在所不惜的念头，热血少当家挥出全力以赴的一刀，\\n开拓自己与家人的未来。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·仇鼠·空手干架的啾座的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以前方第一名角色为中心，\\n\u3000对一定范围内的敌人造成物理伤害（大）。\\n·对前方第一名角色造成物理伤害（小），\\n\u3000中幅降低其物理防御力。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (315300, '噬魂凶蛇', 315300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】通过塔的魔力显现的凶猛灵蛇。\\n张开充满怨恨的大口，贪婪地吞食冒险者的灵魂。\\n\\n·噬魂凶蛇会根据持有的“吸魂”\\n\u3000个数，强化自身的普通攻击\\n\u3000和各技能的效果。\\n·每发动一次普通攻击，增加6个“吸魂”。\\n·必杀技能会根据噬魂凶蛇\\n\u3000持有的“吸魂”个数，对敌方全体造成\\n\u3000一定比例的魔法伤害，\\n\u3000并增加5个“吸魂”。\\n·对敌方全体造成魔法伤害（小）的同时减少其技能值，\\n\u3000并展开降低技能值上升量的\\n\u3000领域。技能值减少量和领域的效果值\\n\u3000会随着噬魂凶蛇持有的“吸魂”个数\\n\u3000增多而强化。\\n·每进行一次攻击，噬魂凶蛇便增加1个“吸魂”\\n\u3000并召唤2只分身，同时将敌方全体\\n\u3000小幅度击飞。\\n·战斗开始时，中幅提升自身的物理攻击力和\\n\u3000魔法攻击力，但会处于每次受到伤害，\\n\u3000噬魂凶蛇都会减少1个“吸魂”的状态。\\n·剩余生命值低于30%时，会大幅提升魔法攻击力，\\n\u3000并增加20个“吸魂”。');
