insert into unit_enemy_data values (315401, '强欲暴君·赌场之王', 315400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】自私残暴的强欲暴君。\\n通过“龙之卵”以暴力消灭敌人。\\n\\n·必杀技能会根据强欲暴君·赌场之王的物理攻击力和\\n\u3000角色的最大生命值，对前方第一名角色造成\\n\u3000一定比例的物理伤害。\\n·展开屏障，极大幅度提升强欲暴君·赌场之王自身的\\n\u3000物理/魔法防御力。\\n·强欲暴君·赌场之王自身的生命值低于50%时，\\n\u3000会展开更为激烈强力的攻击。\\n·掷骰子，提升强欲暴君·赌场之王\\n\u3000自身的物理攻击力。根据掷出的点数，物理攻击力\\n\u3000提升的效果也会有所变化。\\n\u3000掷出1时，小幅提升物理攻击力。\\n\u3000掷出2和3时，中幅提升物理攻击力。\\n\u3000掷出4和5时，大幅提升物理攻击力。\\n\u3000掷出6时，极大幅度提升物理攻击力。\\n\u3000强欲暴君·赌场之王的生命值低于50%时，\\n\u3000就会使用该技能。\\n·对前方第一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (315402, '强欲暴君·赌场之王', 315400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】自私残暴的强欲暴君。\\n通过“龙之卵”以暴力消灭敌人。\\n\\n·必杀技能会根据强欲暴君·赌场之王的物理攻击力和\\n\u3000角色的最大生命值，对前方第一名角色造成\\n\u3000一定比例的物理伤害。\\n·展开屏障，极大幅度提升强欲暴君·赌场之王自身的\\n\u3000物理/魔法防御力。\\n·强欲暴君·赌场之王自身的生命值低于50%时，\\n\u3000会展开更为激烈强力的攻击。\\n·掷骰子，提升强欲暴君·赌场之王\\n\u3000自身的物理攻击力。根据掷出的点数，物理攻击力\\n\u3000提升的效果也会有所变化。\\n\u3000掷出1时，小幅提升物理攻击力。\\n\u3000掷出2和3时，中幅提升物理攻击力。\\n\u3000掷出4和5时，大幅提升物理攻击力。\\n\u3000掷出6时，极大幅度提升物理攻击力。\\n\u3000强欲暴君·赌场之王的生命值低于50%时，\\n\u3000就会使用该技能。\\n·对前方第一名角色造成物理伤害（大）。');
