insert into unit_enemy_data values (315404, '强欲暴君·赌场之王', 315404, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】无数疯狂的游戏机袭来。\\n摇动把手后所显示的赌局结果是生还是死？\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·强欲暴君·赌场之王和赌博机的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据强欲暴君·赌场之王的物理攻击力和\\n\u3000角色的最大生命值，对前方第一名角色\\n\u3000造成一定比例的物理伤害。\\n·战斗开始时，召唤3台赌博机，\\n\u3000并击退敌方全体。经过一定时间后，\\n\u3000再次召唤赌博机。\\n\u3000赌博机对前方第一名角色造成\\n\u3000物理伤害（中）。若赌博机的生命值降至一定值以下，\\n\u3000则大幅降低我方全体的物理防御力和\\n\u3000魔法防御力。\\n·对前方第一名角色造成物理伤害（小）。');
insert into unit_enemy_data values (315405, '强欲暴君·赌场之王', 315405, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】傲慢的无赖男靠“龙之卵”的力量击退了\\n少女们，脸上浮现出嘲弄的笑容。\\n带着绝不屈服的决心，穿过那诡谲辉煌的屏障吧。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·展开屏障，完全防御敌人的攻击。\\n\u3000屏障的耐久度与伤害量无关，\\n\u3000每次受到攻击都会逐渐减少耐久度，\\n\u3000受到300次攻击后，\\n\u3000屏障就会破裂。');
insert into unit_enemy_data values (315406, '强欲暴君·赌场之王', 315406, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】赌场之王释放出秘宝的真正力量，在\\n庞大贪欲的驱使下，要葬送阻挠者的生命。\\n怪盗少女，立刻粉碎暴君的野心吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·强欲暴君·赌场之王的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据强欲暴君·赌场之王的物理攻击力和\\n\u3000角色的最大生命值，对前方第一名角色\\n\u3000造成一定比例的物理伤害。\\n·展开屏障，极大幅度提升强欲暴君·赌场之王自身的\\n\u3000物理/魔法防御力。\\n·强欲暴君·赌场之王自身的生命值低于50%时，\\n\u3000会展开更为激烈强力的攻击。\\n·掷骰子，提升强欲暴君·赌场之王\\n\u3000自身的物理攻击力。根据掷出的点数，物理攻击力\\n\u3000提升的效果也会有所变化。\\n\u3000掷出1时，小幅提升物理攻击力。\\n\u3000掷出2和3时，中幅提升物理攻击力。\\n\u3000掷出4和5时，大幅提升物理攻击力。\\n\u3000掷出6时，极大幅度提升物理攻击力。\\n\u3000强欲暴君·赌场之王的生命值低于50%时，\\n\u3000就会使用该技能。\\n·对前方第一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (315500, '滴水嘴兽', 315500, 0, 16, 270, 3000, 1, 2.0, 0, 0, 0, '【物理】坚守已故主君命令的沙岩像。\\n用愈发猛烈的攻击将冒险者逼入绝境。\\n\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（小）。\\n\u3000该技能造成的伤害\\n\u3000不会回复技能值。\\n·对距离最近的一名敌人造成物理伤害（小）。');
