insert into unit_enemy_data values (316203, '阿斯特莱亚类菌生物', 316200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】会用假蘑菇引诱猎物的可怕蘑菇怪物。\\n吃下蘑菇的人将被困于幻觉中，\\n在毫不知情的情况下成为滋养大自然的养分。\\n\\n·必杀技能会根据阿斯特莱亚类菌生物的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000物理比例伤害，并使生命值在40%以下的敌人\\n\u3000陷入魅惑状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·以自身为中心，使一定范围内的敌人陷入魅惑状态，\\n\u3000并大幅提升物理攻击力和魔法攻击力\\n\u3000直到战斗结束。\\n·阿斯特莱亚类菌生物自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。\\n·对前方第一名角色造成物理伤害（大）。');
insert into unit_enemy_data values (316204, '阿斯特莱亚类菌生物', 316204, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】率领众多仆从，\\n蘑菇怪物的攻势进一步加强。成为捕食者的\\n异类异形们如今正要拉开反击的序幕。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·手下的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·战斗开始时，召唤4只手下，\\n\u3000并击退敌方全体。\\n\u3000手下对前方第一名角色造成\\n\u3000物理伤害（中）。\\n\u3000每打倒1只手下，\\n\u3000手下的剩余数就减1。之后再次召唤手下，\\n\u3000并击退敌方全体。');
insert into unit_enemy_data values (316205, '阿斯特莱亚类菌生物', 316205, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】大自然的威猛不止停留在幻觉之中，\\n仆从展开了激烈的麻痹攻击。其威力之大，\\n会让猎物连指尖都动弹不得。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·阿斯特莱亚类菌生物的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据阿斯特莱亚类菌生物的\\n\u3000物理攻击力和角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·对前方第一名角色造成物理伤害（大）。\\n·在手下收集孢子期间，特大幅度降低\\n\u3000阿斯特莱亚类菌生物的物理防御力\\n\u3000和魔法防御力。之后，手下释放孢子，使前方三名角色\\n\u3000陷入麻痹状态。\\n\u3000在手下收集孢子期间，如果阿斯特莱亚类菌生物\\n\u3000受到700000以上的伤害，则\\n\u3000阿斯特莱亚类菌生物和手下会陷入昏迷，\\n\u3000特大幅度降低物理防御力和魔法防御力。');
insert into unit_enemy_data values (316206, '阿斯特莱亚类菌生物', 316206, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】赌上双方性命的狩猎时间迎来了终局。是狩猎，还是被狩猎。\\n苍翠的战场上，最后的孢子四散开来。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·阿斯特莱亚类菌生物的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据阿斯特莱亚类菌生物的物理攻击力\\n\u3000和角色的最大生命值，对敌方全体造成\\n\u3000物理比例伤害，并使生命值在40%以下的敌人\\n\u3000陷入魅惑状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·以自身为中心，使一定范围内的敌人陷入魅惑状态，\\n\u3000并大幅提升物理攻击力和魔法攻击力\\n\u3000直到战斗结束。\\n·阿斯特莱亚类菌生物自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。\\n·对前方第一名角色造成物理伤害（大）。');
