insert into unit_enemy_data values (316301, '海将海马指挥官', 316300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】以强韧的肉体操纵三叉戟的海兽。因被人类捕获而\\n横冲直撞，令沙滩上的人陷入恐慌。\\n\\n·必杀技能会对前方第三名角色造成\\n\u3000魔法伤害（大），并以该角色为中心，\\n\u3000令小范围内的敌人陷入昏迷。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·大幅提升自身的魔法攻击力，\\n\u3000中幅回复技能值。\\n·对前方第三名角色\\n\u3000造成物理伤害（中）。');
insert into unit_enemy_data values (316302, '海将海马指挥官', 316300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】以强韧的肉体操纵三叉戟的海兽。因被人类捕获而\\n横冲直撞，令沙滩上的人陷入恐慌。\\n\\n·必杀技能会对前方第三名角色造成\\n\u3000魔法伤害（大），并以该角色为中心，\\n\u3000令小范围内的敌人陷入昏迷。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·大幅提升自身的魔法攻击力，\\n\u3000中幅回复技能值。\\n·对前方第三名角色\\n\u3000造成物理伤害（中）。');
insert into unit_enemy_data values (316303, '海将海马指挥官', 316300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】以强韧的肉体操纵三叉戟的海兽。因被人类捕获而\\n横冲直撞，令沙滩上的人陷入恐慌。\\n\\n·必杀技能会对前方第三名角色造成\\n\u3000魔法伤害（大），并以该角色为中心，\\n\u3000令小范围内的敌人陷入昏迷。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·大幅提升自身的魔法攻击力，\\n\u3000中幅回复技能值。\\n·对前方第三名角色\\n\u3000造成物理伤害（中）。\\n·海将海马指挥官的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击，\\n\u3000但自身的物理防御力也会降低。');
insert into unit_enemy_data values (316304, '海将海马指挥官', 316304, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】海将有用压倒性的实力，\\n轻轻一挥就能产生激流袭击人们。\\n鼓起勇气，面对激流吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·海将海马指挥官的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会无视敌人的挑衅，对前方第三名角色\\n\u3000造成魔法伤害（大），\\n\u3000并以该角色为中心，\\n\u3000令小范围内的敌人陷入昏迷。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中），\\n\u3000大幅降低其魔法防御力并将其大幅击飞。\\n\u3000随后大幅回复自身的技能值。\\n·无视挑衅，对前方第三名敌人\\n\u3000造成物理伤害（中）。');
insert into unit_enemy_data values (316305, '海将海马指挥官', 316305, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】因海将的愤怒而引发了海市蜃楼，少女们的幻影袭击而来。为了全身心投入到夏天里，\\n跨过这个试炼吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·海将海马指挥官的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·召唤日和莉、怜和优衣的暗影。\\n·对前方第三名角色\\n\u3000造成物理伤害（中）。');
insert into unit_enemy_data values (316306, '海将海马指挥官', 316305, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】怒气达到了最高峰，海将赌上一切\\n发起了猛攻。为了重获平静，\\n将心底的爱意与友情化作力量，打倒强敌吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·海将海马指挥官的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n\\n·必杀技能会对前方第三名角色造成\\n\u3000魔法伤害（大），并以该角色为中心，\\n\u3000令小范围内的敌人陷入昏迷。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·大幅提升自身的魔法攻击力，\\n\u3000中幅回复技能值。\\n·对前方第三名角色\\n\u3000造成物理伤害（中）。\\n·海将海马指挥官的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击，\\n\u3000但自身的物理防御力也会降低。');
