insert into unit_enemy_data values (316401, '月光之门', 316400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因应月圆月缺而出现，\\n连接两个世界的大门。引导小缪~\\n前往大门，让它回到原来的世界吧！\\n\\n·必杀技能会根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n·击退敌方全体，中幅降低其行动速度。同时根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n\u3000随后赋予月光之门10个\\n\u3000【月之光印】。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成魔法伤害（小）。\\n\u3000同时根据月光之门的【月之光印】数量，\\n\u3000以月光之门为中心，对范围内的所有敌人造成魔法伤害（小）。\\n·【月之光印】最多可赋予50个。');
insert into unit_enemy_data values (316402, '月光之门', 316400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因应月圆月缺而出现，\\n连接两个世界的大门。引导小缪~\\n前往大门，让它回到原来的世界吧！\\n\\n·必杀技能会根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n·击退敌方全体，中幅降低其行动速度。同时根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n\u3000随后赋予月光之门10个\\n\u3000【月之光印】。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成魔法伤害（小）。\\n\u3000同时根据月光之门的【月之光印】数量，\\n\u3000以月光之门为中心，对范围内的所有敌人造成魔法伤害（小）。\\n·【月之光印】最多可赋予50个。');
insert into unit_enemy_data values (316403, '月光之门', 316400, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因应月圆月缺而出现，\\n连接两个世界的大门。引导小缪~\\n前往大门，让它回到原来的世界吧！\\n\\n·必杀技能会根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n·击退敌方全体，中幅降低其行动速度。同时根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n\u3000随后赋予月光之门10个\\n\u3000【月之光印】。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成魔法伤害（小）。\\n\u3000同时根据月光之门的【月之光印】数量，\\n\u3000以月光之门为中心，对范围内的所有敌人造成魔法伤害（小）。\\n·剩余体力低于50%时，\\n\u3000中幅提升月光之门的物理防御力和魔法防御力。\\n\u3000随后赋予月光之门20个\\n\u3000【月之光印】，改变行动模式。\\n·【月之光印】最多可赋予50个。');
insert into unit_enemy_data values (316404, '月光之门', 316406, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】为共度了一个夏天的小缪\\n感到惋惜，由此而生的【小小甜心】\\n的影子。无情地堵住了通往大门的路。\\n\\n·战斗开始时，小幅提升月光之门的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中防御力降低技能\\n\u3000的影响。');
insert into unit_enemy_data values (316405, '月光之门', 316405, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】被通过大门传来的异世界魔力强化的海上魔物。压倒性的魔力奔流\\n给予了少女们更强的试炼。\\n\\n·战斗开始时，小幅提升月光之门的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·该战斗中所有怪物造成的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·中幅提升除月光之门以外，\\n\u3000我方的物理攻击力和魔法攻击力。\\n·当月光之门受到伤害时，\\n\u3000极大幅度提升除月光之门以外，\\n\u3000我方的物理防御力和魔法防御力。');
insert into unit_enemy_data values (316406, '月光之门', 316404, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】连接两个世界最后的大门。\\n将无可取代的夏日回忆藏于心中，\\n引导小小的友人前往月光照耀的彼方吧！\\n\\n·战斗开始时，小幅提升月光之门的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·月光之门的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n·击退敌方全体，中幅降低其行动速度。同时根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成\\n\u3000一定比例的魔法伤害。\\n\u3000随后赋予月光之门15个\\n\u3000【月之光印】。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据月光之门的【月之光印】数量，\\n\u3000对敌方全体造成魔法伤害（中）。\\n\u3000同时根据月光之门的【月之光印】数量，\\n\u3000以月光之门为中心，对范围内的所有敌人造成魔法伤害（大）。\\n·剩余体力低于40%时，\\n\u3000中幅提升月光之门的物理防御力和魔法防御力。\\n\u3000随后赋予月光之门40个\\n\u3000【月之光印】，改变行动模式。\\n·【月之光印】最多可赋予70个。');
