insert into unit_enemy_data values (316501, '哀执的植物羊', 316500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】住在边远大树中的传说中的植物羊。\\n一直期望能与老人再相会，\\n这段漫长的时间将它逼成了拒绝别人的魔兽。\\n\\n·哀执的植物羊每秒会增加\\n\u30003根【忧郁荆棘】。\\n·哀执的植物羊每次受到伤害时，\\n\u3000其身上的【忧郁荆棘】都会减少一根。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对剩余生命值最少的三名敌人\\n\u3000造成一定比例的物理伤害。\\n·哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对前方三名角色造成一定比例的物理伤害。');
insert into unit_enemy_data values (316502, '哀执的植物羊', 316500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】住在边远大树中的传说中的植物羊。\\n一直期望能与老人再相会，\\n这段漫长的时间将它逼成了拒绝别人的魔兽。\\n\\n·哀执的植物羊每秒会增加\\n\u30003根【忧郁荆棘】。\\n·哀执的植物羊每次受到伤害时，\\n\u3000其身上的【忧郁荆棘】都会减少一根。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对剩余生命值最少的三名敌人\\n\u3000造成一定比例的物理伤害。\\n·哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对前方三名角色造成一定比例的物理伤害。');
insert into unit_enemy_data values (316503, '哀执的植物羊', 316500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】住在边远大树中的传说中的植物羊。\\n一直期望能与老人再相会，\\n这段漫长的时间将它逼成了拒绝别人的魔兽。\\n\\n·哀执的植物羊每秒会增加\\n\u30004根【忧郁荆棘】。\\n·哀执的植物羊每次受到伤害时，\\n\u3000其身上的【忧郁荆棘】都会减少一根。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对剩余生命值最少的三名敌人\\n\u3000造成一定比例的物理伤害。\\n·哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对前方三名角色造成一定比例的物理伤害。\\n·剩余体力少于25%时，\\n\u3000赋予哀执的植物羊35根【忧郁荆棘】。');
insert into unit_enemy_data values (316504, '哀执的植物羊', 316504, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】被名为孤独和猜疑的皮毛包裹，\\n犹如笼中困兽，\\n森林中的魔物们挡在了冒险者面前。打败魔物，创出一条道吧。\\n\\n·战斗开始时，小幅提升哀执的植物羊的\\n\u3000物理防御力和魔法防御力。通过本防御\\n\u3000提升效果所增加的防御力，\\n\u3000不受战斗中防御力降低技能的影响。\\n·与哀执的植物羊在一起的怪物造成的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·哀执的植物羊不会因环境效果\\n\u3000而受到伤害。\\n·对剩余生命值最少的三名敌人\\n\u3000造成一定比例的物理伤害。');
insert into unit_enemy_data values (316505, '哀执的植物羊', 316505, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】悲伤的羊将皮毛硬化成铠甲，\\n拒绝一切。不管是刀刃、拳头还是魔法，\\n亦或是伸向它的手。\\n\\n·战斗开始时，中幅提升哀执的植物羊的\\n\u3000物理防御力，小幅提升魔法防御力。\\n\u3000通过本防御提升效果所增加的\\n\u3000防御力，不受战斗中防御力降低技能\\n\u3000的影响。\\n·哀执的植物羊的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并对敌方全体造成一定比例的物理伤害。\\n·哀执的植物羊受到中毒、猛毒、诅咒、烧伤、毒咒效果时，\\n\u3000造成的伤害会减少。\\n·对剩余生命值最少的三名敌人\\n\u3000造成物理伤害（大）。\\n·哀执的植物羊受到中毒、猛毒、诅咒、烧伤、毒咒效果时，\\n\u3000造成的伤害会减少。\\n·自身受到中毒、猛毒、诅咒、烧伤效果时，\\n\u3000特大幅度降低其物理、魔法防御力。');
insert into unit_enemy_data values (316506, '哀执的植物羊', 316506, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】在与老年旅人的约定和胆小的心之间摇摆不定，\\n其纠结化为忧郁荆棘，疯狂起来。\\n面对其暴威，将想法传达给它吧。\\n\\n·战斗开始时，小幅提升哀执的植物羊的\\n\u3000物理防御力和魔法防御力。通过本防御\\n\u3000提升效果所增加的防御力，\\n\u3000不受战斗中防御力降低技能的影响。\\n·哀执的植物羊的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n\\n·哀执的植物羊每秒会增加\\n\u30004根【忧郁荆棘】。\\n·哀执的植物羊每次受到伤害时，\\n\u3000其身上的【忧郁荆棘】都会减少一根。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·根据哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对剩余生命值最少的三名敌人\\n\u3000造成一定比例的物理伤害。\\n·哀执的植物羊的【忧郁荆棘】数量，\\n\u3000对前方三名角色造成一定比例的物理伤害。\\n·剩余体力少于25%时，\\n\u3000赋予哀执的植物羊20根【忧郁荆棘】。');
insert into unit_enemy_data values (316600, '骑兵哥布林', 316600, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】人龙合而为一进行战斗的哥布林骑士。\\n敏捷地穿梭于空中，袭击地上的敌人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·骑兵哥布林的所有伤害\\n\u3000都会大幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最远的三名敌人造成物理伤害（大），\\n\u3000并赋予其眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最远的两名敌人造成物理伤害（小），\u3000中幅降低其物理防御力。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·普通攻击会无视挑衅，对最远的一名敌人\\n\u3000造成物理伤害（中）。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。');
insert into unit_enemy_data values (316601, '骑兵哥布林', 316601, 0, 6, 270, 1000, 1, 1.5, 0, 0, 0, '【物理】人龙合而为一进行战斗的哥布林骑士。\\n敏捷地穿梭于空中，袭击地上的敌人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，展开让敌方全体技能值上升量\\n\u3000小幅下降的领域。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·骑兵哥布林的所有伤害\\n\u3000都会大幅降低队伍的\\n\u3000技能值回复量。\\n·敌人的所有伤害\\n\u3000都会大幅降低骑兵哥布林的技能值回复量。\\n·必杀技能会对最远的三名敌人造成物理伤害（大），\\n\u3000并赋予其眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最远的两名敌人造成物理伤害（小），\u3000大幅降低其物理防御力。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·普通攻击会无视挑衅，对最远的一名敌人\\n\u3000造成物理伤害（中）。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。');
