insert into unit_enemy_data values (316704, '狂乱的博主·镜', 316704, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】被欲望支配的敌人\\n在舞台上纵火，令表演更加激烈。就像是在预示着他的结局一样，\\n舞台火花四溅。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\u3000\\n·狂乱的博主·镜的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·战斗开始时，为狂乱的博主\\n\u3000赋予99个【点赞】。\\n·必杀技能会对前方一名敌人\\n\u3000造成物理伤害（特大）。根据狂乱的博主·镜\\n\u3000所拥有的【点赞】数\\n\u3000提高伤害量。\\n·对前方二名角色造成物理伤害（大）。\\n·战斗开始时，展开战斗领域。\\n\\n【战斗领域效果】\\n·特大幅度降低狂乱的博主·镜\\n\u3000的物理防御力和魔法防御力。\\n·特大幅度降低队伍所有人的物理防御力、魔法防御力\\n\u3000和生命值吸收，每秒造成\\n\u3000物理伤害（小）。');
insert into unit_enemy_data values (316705, '狂乱的博主·镜', 316705, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】敌人的激情演出能让\\n非人的东西也成为同伴。为了击退紧逼而至的威胁，\\n终结狂乱的舞台——赶紧前往传播者的身边吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\u3000\\n·狂乱的博主·镜的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·召唤智、铃和七七香的暗影。\\n·对前方二名角色造成物理伤害（大）。');
insert into unit_enemy_data values (316706, '狂乱的博主·镜', 316705, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】舞台即将迎来终结，\\n祭坛上剩下的只有充满欲望的魔物。贯彻自身的信念，击倒敌人吧。\\n为了守护大家的笑容——以及“喜欢”的心情。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\u3000\\n·狂乱的博主·镜的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·战斗开始时，为狂乱的博主\\n\u3000赋予十个【点赞】。\\n·必杀技能会对前方一名敌人\\n\u3000造成物理伤害（特大）。根据狂乱的博主·镜\\n\u3000所拥有的【点赞】数\\n\u3000提高伤害量。并赋予15个【点赞】。\\n·特大幅降低狂乱的博主·镜的物理防御力\\n\u3000和魔法防御力，并赋予其\\n\u300010个【点赞】。同时根据【点赞】数提升自身的物理攻击力\\n\u3000和技能值。\\n·对前方二名角色造成物理伤害（大）。赋予狂乱的博主·镜\\n\u30005个【点赞】。\\n·自身生命值低于50%时，\\n\u3000大幅提升狂乱的博主·镜的物理攻击力和行动速度。\\n·当狂乱的博主·镜受到毒、猛毒、诅咒、\\n\u3000烧伤及毒咒效果时，\\n\u3000减少狂乱的博主·镜的5个【点赞】。');
insert into unit_enemy_data values (316800, '黑之王炸脖龙', 316800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】因为米洛克而再次从狭界中显现的黑之王。企图用黑暗的瘴气蹂躏世界。\\n唯有希望之力能对抗黑之王。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（大），\\n\u3000并赋予其中毒状态。\\n·对前方三名角色造成物理伤害（中），\\n\u3000并大幅降低其物理防御力和技能值。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成物理伤害（中），\\n\u3000并大幅降低其物理防御力。\\n\u3000此外，当角色的生命值小于最大生命值的50%时，\\n\u3000中幅降低其行动速度。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·黑之王炸脖龙自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。\\n·各个目标处于击破状态时，其物理防御力和\\n\u3000魔法防御力将分别大幅度降低。');
insert into unit_enemy_data values (316900, '失控的圣洁之弓', 316900, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】接收了米洛克极大的能量而再次失控的\\n拉菲的圣弓。被它的箭矢射中会变成\\n无力的天使，生命将被扭曲的诅咒侵蚀。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并大幅降低其魔法防御力。\\n\u3000若其处于天使状态，则赋予诅咒。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并使生命值不足70%的角色在一定时间内进入\\n\u3000天使状态。\\n\u3000变成天使的角色\\n\u3000只能使用普通攻击，身上的属性值提升效果\\n\u3000将被解除。\\n\u3000回复生命值即可\\n\u3000解除天使状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方三名角色造成魔法伤害（中），\\n并使其眩晕。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·失控的圣洁之弓自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (317000, '幻影男爵', 317000, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】被不祥诅咒从幽冥引诱而来的悲伤的亡亲之灵。本应升天的灵魂被\\n米洛克的法术变成庞大的魔力，无情地屠杀一切挡路者。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对最近的一名敌人造成魔法伤害（大），\\n\u3000并大幅度降低其魔法防御力。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对距离最远的一名敌人造成物理伤害（中），\\n\u3000并将其拉至距离最近的角色的位置，\\n\u3000同时使其眩晕。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对距离最近的一名敌人造成\\n\u3000魔法伤害（中）。\\n\u3000此外，对前方三名角色造成\\n\u3000魔法伤害（中），\\n\u3000并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·幻影男爵的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (317100, '腾特帕斯', 317100, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】作为饵食被暴食之女\\n生吞的愤怒的软体魔兽。身上的齿印便是魔兽的决心。\\n借助米洛克的力量，愤怒地要为自己报仇雪恨。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的物理伤害。\\n·根据角色的最大生命值，对敌方全体造成\\n\u3000物理比例伤害，并大幅降低其物理防御力\\n\u3000且使其麻痹。\\n\u3000这个技能的效果和异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对眼前一名敌人造成\\n\u3000物理伤害（大）。\\n·根据角色的最大生命值，\\n\u3000对前方一名敌人造成一定比例的物理伤害。\\n·腾特帕斯自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (317200, '高辛', 317200, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】从《救星》处获得力量，得以改头换面的\\n原贵族议会议长。使用黑暗之力，强力地粉碎敌方防线，\\n从眼前的敌人开始进行痛恨的单体攻击。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对前方一名敌人\\n\u3000造成物理伤害（极大）。\\n·对前方一名敌人造成物理伤害（大），\\n\u3000并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·大幅提升自身的物理攻击力。\\n·对前方一名敌人造成物理伤害（大），\u3000中幅降低其物理防御力。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·高辛自身的生命值低于50%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (317300, '阿尔玛', 317300, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】从远古开始守护迷宫的防卫兵器。\\n因米洛克的黑暗之力而变得邪恶的守护之力，\\n化为粉碎一切的灾厄矗立在世人面前。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成\\n\u3000物理伤害（大）。\\n\\n·对敌方全体造成魔法伤害（中），并大幅度降低其物理防御力\\n\u3000和魔法防御力。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对眼前一名敌人造成\\n\u3000物理伤害（大）。\\n·阿尔玛自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (317400, '奥拉姆', 317400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在遥远的古代诞生的防卫兵器。\\n古代兵器的诅咒被米洛克的魔能解放，\\n灾厄如洪水般无情地向人们袭来。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成\\n\u3000魔法伤害（大）。\\n·对敌方全体造成物理伤害（中），并中幅度降低\\n\u3000其物理攻击力和魔法攻击力。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并在短时间内使其眩晕。\\n\u3000如角色的生命值小于最大生命值的40％，\\n\u3000使其陷入长时间的眩晕状态。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·奥拉姆自身的生命值低于70%时，\\n\u3000会展开更为激烈凶暴的攻击。\\n');
insert into unit_enemy_data values (317500, '好朋友X', 317500, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】钢铁的机械巨人再次袭来，怪诞的艺术\\n与灾厄融为一体。用无情的咒语\\n再次破坏学徒们的自我。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成\\n\u3000魔法伤害（大），\\n\u3000并赋予其烧伤状态。\\n\u3000提升对处于中毒状态的敌人造成的烧伤伤害。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·中幅提升自身的物理攻击力后，\\n\u3000对敌方全体造成物理伤害（中）。\\n\u3000此外，将解除敌方全体的屏障。\\n·对敌方全体造成魔法伤害（中）。当敌人处于中毒状态时，\\n\u3000特大幅降低其行动速度。\\n\u3000这个技能的效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·好朋友X自身的生命值低于40%时，\\n\u3000会展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (317600, '无名艺术', 317600, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】再次为人类带来超前的畸形艺术，\\n毒舌机器人与灾厄融为一体。作者的奇才如今竟变成了\\n毁灭世界的爪牙……？\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成\\n\u3000魔法伤害（大），\\n\u3000并赋予其中毒状态。\\n·对敌方全体造成物理伤害（大）。\\n\u3000在敌人处于中毒状态时，赋予追加伤害。\\n·无名艺术自身的生命值低于40%时，\\n\u3000会特大幅度提升己方全员的技能值，\\n\u3000并展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (317700, '米洛克', 317700, 0, 16, 270, 3000, 2, 1.6, 317700, 317700, 0, '【模式1】幽禁人的灵魂，将世界\\n导向终焉的虚假救世主。使用特殊的法术，施展强力的魔法和赋予各种异常状态\\n夺走所有抵抗者的生命。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会根据角色的剩余生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n\u3000并展开一个使敌方全体的技能值每秒小幅下降，\\n\u3000而米洛克的技能值每秒小幅上升的\\n\u3000领域。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并赋予黑炎状态。\\n\\n处于黑炎状态的敌人将每秒\\n\u3000受到伤害。由黑炎造成的伤害\\n\u3000每秒提升10%。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
