insert into unit_enemy_data values (317800, '米洛克', 317800, 0, 16, 270, 3000, 2, 1.6, 317700, 317700, 0, '【模式2】顺着将世界导向终焉的火炎，\\n救世主发动秘密仪式。使魔力无效化，\\n时时刻刻地破坏人们的攻势，粉碎人们的羁绊。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，吸收敌方全体的生命值，提升技能值，\\n\u3000大幅降低物理攻击力、魔法攻击力、\\n\u3000物理防御力及魔法防御力，直至战斗结束。\\n·必杀技能会根据角色的剩余生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (317900, '米洛克', 317900, 0, 16, 270, 3000, 2, 1.6, 317700, 317700, 0, '【模式3】释放真正的力量，\\n使魔力失控的米洛克。魔法的威力变得更为猛烈，\\n消灭一切与神背道而驰的敌人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成\\n\u3000其剩余生命值的99%的魔法伤害。\\n·对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其魔法防御力，同时解除屏障。\\n·大幅提升自身的魔法攻击力和技能值。\\n·米洛克自身的生命值低于1600万时，\\n\u3000会改变行动模式，并展开\\n\u3000更为激烈凶暴的攻击。');
insert into unit_enemy_data values (318000, '黑曜石魔像', 318000, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】为了破坏和平而被制造出来的\\n坚固魔物。散发着黑暗光辉的身体无人能破，\\n以压倒性的质量与力量著称。\\n\\n·必杀技能会减少敌方全体\\n\u300015%的最大生命值，并造成物理伤害（小）。减少的生命值直到战斗结束\\n\u3000都无法恢复。\\n\u3000由这个技能造成的伤害无法回复技能值，\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·小幅回复自身的技能值。\\n·对敌方全体造成物理伤害（中），并小幅度降低\\n\u3000其物理防御力，直至战斗结束。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方第一名角色造成物理伤害（中）。');
