insert into unit_enemy_data values (318302, '祈福引灾的招财猫', 318300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因执着于自身的使命而化为诅咒之物的\\n招财猫。过于执着的愿望不只能招来幸福，\\n还会引来灾祸。\\n\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成魔法伤害（大），\\n\u3000使其陷入眩晕并中幅降低其技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中）。无视挑衅，\\n\u3000把最近的一名敌人拉到\\n\u3000自身前面，将附近的其余敌人\\n\u3000小幅拖向自身。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小）。\\n·剩余时间低于50秒时，以自身为中心，\\n\u3000展开一个能特大幅度降低一定范围内的敌人的\\n\u3000物理攻击力和魔法攻击力，\\n\u3000的屏障。');
insert into unit_enemy_data values (318303, '祈福引灾的招财猫', 318300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】因执着于自身的使命而化为诅咒之物的\\n招财猫。过于执着的愿望不只能招来幸福，\\n还会引来灾祸。\\n\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成魔法伤害（大），\\n\u3000使其陷入眩晕并中幅降低其技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中）。无视挑衅，\\n\u3000把最近的一名敌人拉到\\n\u3000自身前面，将附近的其余敌人\\n\u3000小幅拖向自身。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小）。\\n·剩余时间低于50秒时，以自身为中心，\\n\u3000展开一个能特大幅度降低一定范围内的敌人的\\n\u3000物理攻击力和魔法攻击力，\\n\u3000的屏障。');
insert into unit_enemy_data values (318304, '祈福引灾的招财猫', 318304, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】诅咒之物的失控并没有停止，\\n在为数众多的奴仆的跟随下展开更为猛烈的攻势。黑白相间的奴仆们\\n能击退各种敌人。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·祈福引灾的招财猫和随从们的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小）。\\n·召唤随从，击退敌方全体。\\n·每打倒1只被召唤的随从，\\n\u3000剩余目标数就减少1。之后再次召唤随从，\\n\u3000并击退敌方全体。\\n白色招财猫的物理防御力很高，会对最近的一名敌人\\n\u3000造成物理伤害（小）。\\n黑色招财猫的魔法防御力很高，会对最远的一名敌人\\n\u3000造成物理伤害（小）。');
insert into unit_enemy_data values (318305, '祈福引灾的招财猫', 318305, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】招财猫长年累月积聚的哀叹\\n能强制使眼前的一切陷入沉默。甚至能遮盖\\n想要自救的人们发出的呼喊。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·祈福引灾的招财猫的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（中），\\n\u3000并赋予其沉默状态。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并特大幅度提升自身的魔法防御力。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（中）。\\n·对敌方全体造成物理伤害（中）。');
insert into unit_enemy_data values (318306, '祈福引灾的招财猫', 318304, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】发挥招财猫的真正力量，\\n给出致命一击。解除这尽是悲叹的诅咒，\\n夺回充满笑声的咖啡厅吧！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·祈福引灾的招财猫的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n\\n·必杀技能会以最近的一名敌人为中心，\\n\u3000对小范围内的敌人造成魔法伤害（大）并使其眩晕，\\n\u3000同时中幅降低其技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成魔法伤害（小）。\\n·剩余时间低于50秒时，以自身为中心，\\n\u3000展开一个能特大幅度降低一定范围内的敌人的\\n\u3000物理攻击力和魔法攻击力，\\n\u3000并大幅降低其行动速度\\n\u3000的屏障。');
insert into unit_enemy_data values (318400, '波塞冬·海神之剑', 318400, 0, 0, 600, 3000, 1, 1.6, 0, 0, 0, '【物理】于海底深处等待着挑战者的\\n壮阔大海的化身。手中大剑一出，\\n便能将任何挡路的人都切成\\n海中的碎屑。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·波塞冬的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最近一名敌人\\n\u3000造成物理伤害（特大），并中幅降低敌方全体的\\n\u3000物理攻击力和魔法攻击力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名敌人造成物理伤害（中），\\n\u3000中幅降低其物理防御力。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。');
insert into unit_enemy_data values (318401, '波塞冬·大洋之杖', 318401, 0, 0, 600, 3000, 2, 1.6, 0, 0, 0, '【魔法】于海底深处等待着挑战者的\\n壮阔大海的化身。法杖施放的魔法会卷起飓风，\\n对破坏恩泽之海的狂徒降下天谴。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·波塞冬的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最近的一名敌人\\n\u3000造成魔法伤害（中）。之后对敌方全体造成魔法伤害（大），\\n\u3000并使其陷入短时间的眩晕状态。\\n\u3000当角色的生命值低于80％时，\\n\u3000使其陷入长时间的眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中）。\\n\u3000如角色的生命值小于最大生命值的80％，\\n\u3000中幅降低其技能值。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对距离最远的一名敌人造成魔法伤害（大）。');
insert into unit_enemy_data values (318402, '波塞冬·岚击之枪', 318402, 0, 0, 600, 3000, 1, 1.6, 0, 0, 0, '【物理】于海底深处等待着挑战者的\\n壮阔大海的化身。那把枪的凌乱攻击能引起狂暴的旋涡，\\n将世间万物都吸入水底。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·波塞冬的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会以前方第三名敌人为中心，\\n\u3000中幅降低小范围内最多三名角色的\\n物理防御力和魔法防御力，\\n\u3000之后对其造成物理伤害（特大）。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对最近的一名敌人造成物理伤害（大），\\n\u3000并将其击退。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以前方第三名敌人为中心，\\n\u3000对小范围内的敌人造成魔法伤害（特大）。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。\\n·剩余时间少于45秒时，会展开更为激烈凶暴的攻击，\\n\u3000并提升防御力，变得更顽强。\\n·左侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力中幅降低。\\n·中央的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力中幅降低。\\n·右侧的目标处于击破状态时，\\n\u3000自身的物理防御力和魔法防御力中幅降低。');
insert into unit_enemy_data values (318403, '波塞冬·激波之拳', 318403, 0, 0, 600, 3000, 1, 1.6, 0, 0, 0, '【物理】于海底深处等待着挑战者的\\n壮阔大海的化身。拳头挥出的一击是能使\\n广阔无边的海平面一分为二的必杀神技。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·波塞冬的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·对敌方全体造成物理伤害（小）\\n\u3000，并小幅降低其技能值。这个技能在伤害免疫的情况下\\n\u3000也会成功降低技能值。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对距离最近的一名敌人造成物理伤害（小）。');
insert into unit_enemy_data values (318404, '波塞冬·雷击之枪', 318404, 0, 0, 600, 3000, 2, 1.6, 0, 0, 0, '【魔法】在赌上性命的激战之后，\\n海神展现出了无与伦比的真正实力。雷枪如同愤怒的大海一般，\\n仅凭一击便能终止战局。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·波塞冬的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会中幅度降低敌方全体的\\n\u3000物理防御力和魔法防御力，\\n\u3000并对其造成物理伤害（特大）和魔法伤害（特大）。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n·对最近的一名敌人造成物理伤害（大），\\n\u3000并将其击退。同时赋予\\n\u3000眩晕状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（大）。\\n·对最近的一名敌人造成物理伤害（大）和\\n\u3000魔法伤害（大）。\\n·剩余时间少于30秒时，\\n\u3000会展开更为激烈凶暴的攻击。');
