insert into unit_enemy_data values (318501, '乐园的守护者', 318500, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】突然出现在被囚禁在回忆中的\\n兰德索尔里的变异魔物。为了守护乐园，\\n它们手执冰冷双枪阻拦解放者的去路。\\n\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度，\\n\u3000小幅降低其魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·小幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·对前方第一名角色造成魔法伤害（中）。\\n·左侧目标处于击破状态时，\\n\u3000物理/魔法防御力中幅降低。\\n·中央下方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·中央上方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·右侧目标处于击破状态时，\\n\u3000物理/魔法防御力特大幅度降低。');
insert into unit_enemy_data values (318502, '乐园的守护者', 318500, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】突然出现在被囚禁在回忆中的\\n兰德索尔里的变异魔物。为了守护乐园，\\n它们手执冰冷双枪阻拦解放者的去路。\\n\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度，\\n\u3000小幅降低其魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·小幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·对前方第一名角色造成魔法伤害（中）。\\n·左侧目标处于击破状态时，\\n\u3000物理/魔法防御力中幅降低。\\n·中央下方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·中央上方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·右侧目标处于击破状态时，\\n\u3000物理/魔法防御力特大幅度降低。');
insert into unit_enemy_data values (318503, '乐园的守护者', 318500, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【魔法】突然出现在被囚禁在回忆中的\\n兰德索尔里的变异魔物。为了守护乐园，\\n它们手执冰冷双枪阻拦解放者的去路。\\n\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度，\\n\u3000小幅降低其魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·小幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·对前方第一名角色造成魔法伤害（中）。\\n·剩余时间少于25秒时，小幅提升自身的魔法攻击力，\\n\u3000大幅提升技能值。\\n·左侧目标处于击破状态时，\\n\u3000物理/魔法防御力中幅降低。\\n·中央下方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·中央上方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·右侧目标处于击破状态时，\\n\u3000物理/魔法防御力特大幅度降低。');
insert into unit_enemy_data values (318504, '乐园的守护者', 318504, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【模式1】层层坚固的屏障挡住了去路。其拼命保护自己的姿态仿佛与这个扭曲的乐园融为了一体，\\n显得虚幻又无常。\\n\\n·此战斗中，所有目标\\n\u3000击破无效。\\n·展开屏障，完全防御敌人的攻击。\\n\u3000屏障在每次受到伤害时逐渐失去耐久性，\\n\u3000受到一定程度的伤害后\\n\u3000屏障就会破裂。\\n·战斗开始时，大幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度，\\n\u3000小幅降低其魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·小幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·对前方第一名角色造成魔法伤害（中）。\\n\u3000该技能造成的伤害，\\n\u3000会减少技能值回复量。');
insert into unit_enemy_data values (318505, '乐园的守护者', 318505, 0, 16, 270, 3000, 2, 1.4, 0, 0, 0, '【模式2】从回忆深处浮现出最珍视的二人的幻象。为了不让最珍贵的宝物再受到玷污，\\n少女奋起迎战。\\n\\n·此战斗中，所有目标\\n\u3000击破无效。\\n·战斗开始时，大幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n乐园的守护者和随从怪物的所有伤害\\n\u3000都会大幅降低队伍的\\n\u3000技能值回复量。\\n·小幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·对前方第一名角色造成魔法伤害（中）。');
insert into unit_enemy_data values (318506, '乐园的守护者', 318505, 0, 16, 270, 3000, 2, 0.8, 0, 0, 0, '【模式3】阴森恐怖的魔物继续挣扎战斗着，\\n直到最后一刻。以不屈的信念和\\n觉醒之力，开辟未来的道路吧。\\n\\n·战斗开始时，大幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·乐园的守护者的所有伤害\\n\u3000都会大幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体造成魔法伤害（大），\\n\u3000并中幅降低其行动速度，\\n\u3000小幅降低其魔法防御力。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·中幅提升自身的魔法攻击力，大幅提升\\n\u3000行动速度。\\n·对前方第一名角色造成魔法伤害（中）。\\n·剩余体力少于25%时，大幅提升自身的魔法攻击力\\n\u3000和技能值。\\n·左侧目标处于击破状态时，\\n\u3000物理/魔法防御力中幅降低。\\n·中央下方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·中央上方目标处于击破状态时，\\n\u3000物理/魔法防御力大幅降低。\\n·右侧目标处于击破状态时，\\n\u3000物理/魔法防御力特大幅度降低。');
