insert into unit_enemy_data values (318603, '久远之园的模仿者', 318600, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】永远充满幸福的乐园的创造主。拒绝未来，\\n改变自己的外表，阻挡向往明日的人们的去路。\\n\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并根据自身的魔法攻击力和角色的最大生命值\\n\u3000造成一定比例的魔法伤害。若敌人的生命值低于\\n\u300050%，使其陷入束缚状态。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·解除敌方全体的属性提升效果后，\\n\u3000对敌方全体造成魔法伤害（小），\\n\u3000并赋予其沉默状态。同时中幅降低自身的\\n\u3000物理防御力和魔法防御力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n·自身的生命值低于50%时，会改变行动模式，\\n\u3000并展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (318604, '久远之园的模仿者', 318604, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】背弃了明天的创造主，\\n用束缚着自己的锁链把解放者们困在乐园之中。怀着前往未来的决心，\\n斩断层层枷锁吧。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并根据自身的魔法攻击力和角色的最大生命值\\n\u3000造成一定比例的魔法伤害。若敌人的生命值低于\\n\u300050%，使其陷入束缚状态。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n\u3000该技能所造成的伤害\\n\u3000会中幅降低敌方的技能值回复量。\\n·特大幅度提升自身的物理防御力和\\n\u3000魔法防御力，并展开锁链。一定时间后，根据角色的最大生命值，对敌方全体造成一定比例的魔法伤害，\\n\u3000同时使其陷入束缚状态。展开锁链时，当自身受到一定伤害后，\\n\u3000技能便会中断，自身的物理防御力和魔法防御力中幅降低，\\n\u3000直至战斗结束。每使用一次该技能，\\n\u3000都会增加打断技能\\n\u3000所需要的伤害量。\\n·对距离最近的一名敌人造成魔法伤害（小）。\\n\u3000该技能所造成的伤害\\n\u3000会中幅降低敌方的技能值回复量。');
insert into unit_enemy_data values (318605, '久远之园的模仿者', 318605, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】对美好幸福的愿景和未来的不安使其在乐园中催生出\\n解放者们的幻影。把对未来的希望化为力量，\\n击倒虚假的自己吧。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·久远之园的模仿者的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·召唤望、矛依未和嘉夜的暗影。\\n·对距离最近的一名敌人造成魔法伤害（小）。');
insert into unit_enemy_data values (318606, '久远之园的模仿者', 318605, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】连最珍视之人的声音都无法传达，创造主对反抗\\n自己的人使出终极力量。和同伴、搭档一起\\n取回光辉的未来吧。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·久远之园的模仿者的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000并根据自身的魔法攻击力和角色的最大生命值\\n\u3000造成一定比例的魔法伤害。若敌人的生命值低于\\n\u300050%，使其陷入束缚状态。\\n\u3000这个技能的异常状态在伤害免疫的\\n\u3000情况下也会成功赋予。\\n·解除敌方全体的属性提升效果后，\\n\u3000对敌方全体造成魔法伤害（中），\\n\u3000并赋予其沉默状态，\\n\u3000特大幅度降低其生命值吸收。同时中幅降低\\n\u3000自身的物理和\\n\u3000魔法防御力。这个技能的异常状态和效果在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对距离最近的一名敌人造成魔法伤害（大）。\\n·自身的生命值低于50%时，会改变行动模式，\\n\u3000并展开更为激烈凶暴的攻击。');
insert into unit_enemy_data values (318700, '土豪魔像', 318700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】从次元的夹缝中诞生的，活的财宝。\\n一边散落金光闪闪的钱币一边袭击冒险者。\\n·必杀技能会对敌方全体造成物理伤害（大），\\n\u3000并大幅降低其物理攻击力和\\n\u3000魔法攻击力。同时特大幅降低自身的\\n\u3000物理防御力和魔法防御力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成物理伤害（中），并使其眩晕。\\n\u3000之后，特大幅降低自身的物理防御力和\\n\u3000魔法防御力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方三名敌人造成物理伤害（中），\\n\u3000并将其击退。之后特大幅降低自身的\\n\u3000物理防御力和魔法防御力。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对前方一名敌人造成物理伤害（中）。\\n\u3000之后，特大幅降低自身的物理防御力和\\n\u3000魔法防御力。');
