insert into unit_enemy_data values (318801, '猩猩大王', 318800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在森林深处守护着动物们的\\n温柔猿类魔物。却因牙痛而失去了理智，\\n变成了残害森林的破坏者。\\n·必杀技能会对最近一名敌人\\n\u3000造成物理伤害（特大），并使其眩晕。\\n\\n·同时对敌方全体造成物理伤害（大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·以最近的一名敌人为中心，大幅降低小范围内敌人\\n\u3000的物理防御力，\\n\u3000并造成物理伤害（特大）。同时使其眩晕。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n\u3000之后，小幅降低自身的物理防御力和\\n\u3000魔法防御力。\\n·中幅回复自身的技能值，\\n\u3000大幅提升行动速度。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。');
insert into unit_enemy_data values (318802, '猩猩大王', 318800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在森林深处守护着动物们的\\n温柔猿类魔物。却因牙痛而失去了理智，\\n变成了残害森林的破坏者。\\n·必杀技能会对最近一名敌人\\n\u3000造成物理伤害（特大），并使其眩晕。\\n\\n·同时对敌方全体造成物理伤害（大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·以最近的一名敌人为中心，大幅降低小范围内敌人\\n\u3000的物理防御力，\\n\u3000并造成物理伤害（特大）。同时使其眩晕。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n\u3000之后，小幅降低自身的物理防御力和\\n\u3000魔法防御力。\\n·中幅回复自身的技能值，\\n\u3000大幅提升行动速度。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。');
insert into unit_enemy_data values (318803, '猩猩大王', 318800, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】在森林深处守护着动物们的\\n温柔猿类魔物。却因牙痛而失去了理智，\\n变成了残害森林的破坏者。\\n·必杀技能会对最近一名敌人\\n\u3000造成物理伤害（特大），并使其眩晕。\\n\\n·同时对敌方全体造成物理伤害（大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·以最近的一名敌人为中心，大幅降低小范围内敌人\\n\u3000的物理防御力，\\n\u3000并造成物理伤害（特大）。同时使其眩晕。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n\u3000之后，小幅降低自身的物理防御力和\\n\u3000魔法防御力。\\n·中幅回复自身的技能值，\\n\u3000大幅提升行动速度。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·剩余时间少于30秒时，\\n\u3000会展开更为激烈凶暴的攻击。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。');
insert into unit_enemy_data values (318804, '猩猩大王', 318804, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】因痛苦而失去理智的大猩猩，\\n连一直深爱的森林同伴也一同抗拒。为安宁的自然而奋斗的游骑兵，\\n他们的坚定意志正遭受着考验。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·猩猩大王的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最近一名敌人\\n\u3000造成物理伤害（大），并使其眩晕。同时对敌方全体\\n\\n·造成物理伤害（大）。\\n·大幅提升自身的物理攻击力，极大幅提升\\n\u3000行动速度。\\n·对最近的一名敌人造成\\n\u3000物理伤害（大）。');
insert into unit_enemy_data values (318805, '猩猩大王', 318805, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】面对为魔物着想的游骑兵，\\n大猩猩发出能撼动森林的咆哮来迎击。\\n人类和魔物能否互相理解——\\n\\n·战斗开始时，小幅提升自身的物理防御力和\\n\\n魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·猩猩大王的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最近一名敌人\\n\u3000造成物理伤害（特大）。对敌方全体造成物理伤害（大），\\n\u3000并使其眩晕。\\n\u3000该技能所造成的伤害\\n\u3000不会使敌人的技能值回复。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近的一名敌人为中心，大幅降低小范围内敌人\\n\u3000的物理防御力，\\n\u3000并造成物理伤害（特大）。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名敌人造成\\n\u3000物理伤害（大）。\\n·开始蓄力后，每秒中幅回复技能值。\\n\u3000在此期间，特大幅降低自身的\\n\u3000物理防御力和魔法防御力。蓄力时，在自身受到一定伤害后，\\n\u3000技能便会中止，\\n\u3000同时特大幅降低自身的技能值。\\n\u3000蓄力结束后，特大幅提升自身的\\n\u3000物理攻击力。');
insert into unit_enemy_data values (318806, '猩猩大王', 318806, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】为了与魔物共存而展开的战斗，\\n马上便要落下帷幕。并不是为了伤害它，而是为了把想法传达给它，\\n游骑兵勉力一搏！\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·猩猩大王的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对最近一名敌人\\n\u3000造成物理伤害（特大），并使其眩晕。\\n\\n·同时对敌方全体造成物理伤害（大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·以最近的一名敌人为中心，大幅降低小范围内敌人\\n\u3000的物理防御力，\\n\u3000并造成物理伤害（特大）。同时使其眩晕。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n\u3000之后，小幅降低自身的物理防御力和\\n\u3000魔法防御力。\\n·中幅回复自身的技能值，\\n\u3000大幅提升行动速度。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·剩余时间少于30秒时，\\n\u3000会展开更为激烈凶暴的攻击。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。之后小幅降低自身的\\n\u3000物理防御力和魔法防御力。');
