insert into unit_enemy_data values (319104, '睿智的炼金术师·莫布', 319104, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式1】炼金术师用集其研究精髓所制成的钢制铁臂\\n挥出拼尽全力的一击。以能击碎巨岩的冲击力\\n无情地向冒险者们袭来。\\n\\n·战斗开始后，小幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·预兆值积累到最大值时，对最近的一名敌人\\n\u3000造成999999的物理伤害。');
insert into unit_enemy_data values (319105, '睿智的炼金术师·莫布', 319104, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式2】横行无忌的双臂，\\n无论敌人在哪都能顺利捕捉，没有任何空当。努力思考，\\n想办法摆脱这双顽固地追赶的手臂吧。\\n\\n·战斗开始后，小幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·睿智的炼金术师·莫布的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据自身的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并使其短时间内陷入麻痹。\\n\u3000当角色的生命值低于50％时，\\n\u3000使其陷入长时间的麻痹状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。\\n·对敌方全体造成物理伤害（大）。然后将距离最远的\\n二名敌人拉向自己，\\n\u3000并使其眩晕。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以自身为中心，对范围内的敌人造成物理伤害（大），\\n\u3000并大幅降低其物理攻击力和\\n\u3000魔法攻击力。同时将其击退。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。');
insert into unit_enemy_data values (319106, '睿智的炼金术师·莫布', 319104, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【模式3】吸取了失败的教训，\\n不断进化的炼金术师。齐心合力，打倒这个一心想要实现\\n一己私欲的傲慢贤者吧。\\n\\n·战斗开始后，小幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·睿智的炼金术师·莫布的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据自身的物理攻击力，\\n\u3000对敌方全体造成一定比例的物理伤害，\\n\u3000并使其短时间内陷入麻痹。\\n\u3000当角色的生命值低于50％时，\\n\u3000使其陷入长时间的麻痹状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·根据自身的物理攻击力，\\n\u3000对最接近的三名敌人造成一定比例的物理伤害。\\n·中幅提升自身的物理攻击力。\\n·剩余生命值低于50%时，大幅提升\\n\u3000物理攻击力。\\n·对最近的一名敌人造成\\n\u3000物理伤害（特大）。');
insert into unit_enemy_data values (319200, '绝美花妖', 319200, 0, 16, 270, 3000, 2, 2.8, 0, 0, 0, '【物理】拟态成巨型莲花的魔物。\\n从华丽的服饰散发出漫天花瓣，美丽而猛烈地袭向猎物。\\n\\n·必杀技能会对前方二名角色造成\\n\u3000魔法伤害（大），\\n\u3000并使敌方全体陷入麻痹。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n\u3000对前方二名角色造成\\n\u3000魔法伤害（中），\\n\u3000并将其击退。\\n\\n·对前方二名角色造成魔法伤害（中），\\n\u3000并中幅降低其\\n\u3000魔法防御力。');
insert into unit_enemy_data values (319300, '怨恨幽灵', 319300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】对前来休养地的人们发动袭击的恶灵。诅杀屡遭阻止，因被轻视而产生怒气和怨念，\\n在海岸边散播恐惧。\\n\u3000·必杀技能会对敌方全体造成\\n\u3000魔法伤害（特大）。同时小幅降低其物理攻击力和\\n\u3000魔法攻击力，\\n\u3000并在一定时间内大幅降低\\n\u3000生命值回复量。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以自身为中心，对一定范围内所有敌人造成魔法伤害（大），\\n\u3000并赋予诅咒状态。\\n\u3000当敌人的生命值低于60％时，\\n\u3000使其眩晕。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中）。若敌人的生命值低于60%，\\n\u3000使其陷入混乱状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319301, '怨恨幽灵', 319300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】对前来休养地的人们发动袭击的恶灵。诅杀屡遭阻止，因被轻视而产生怒气和怨念，\\n在海岸边散播恐惧。\\n\u3000·必杀技能会对敌方全体造成\\n\u3000魔法伤害（特大）。同时小幅降低其物理攻击力和\\n\u3000魔法攻击力，\\n\u3000并在一定时间内大幅降低\\n\u3000生命值回复量。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以自身为中心，对一定范围内所有敌人造成魔法伤害（大），\\n\u3000并赋予诅咒状态。\\n\u3000当敌人的生命值低于60％时，\\n\u3000使其眩晕。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中）。若敌人的生命值低于60%，\\n\u3000使其陷入混乱状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319302, '怨恨幽灵', 319300, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】对前来休养地的人们发动袭击的恶灵。诅杀屡遭阻止，因被轻视而产生怒气和怨念，\\n在海岸边散播恐惧。\\n\u3000·必杀技能会对敌方全体造成\\n\u3000魔法伤害（特大）。同时小幅降低其物理攻击力和\\n\u3000魔法攻击力，\\n\u3000并在一定时间内大幅降低\\n\u3000生命值回复量。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以自身为中心，对一定范围内所有敌人造成魔法伤害（大），\\n\u3000并赋予诅咒状态。\\n\u3000当敌人的生命值低于60％时，\\n\u3000使其眩晕。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中）。若敌人的生命值低于60%，\\n\u3000使其陷入混乱状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·当自身的生命值低于50%时，会展开\\n\u3000更为激烈凶暴的攻击。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319303, '怨恨幽灵', 319303, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】被悲愤的声音引来的\\n暗影。为了保护作为灾祸根源的恶灵，它们散播邪恶之气，\\n挡在了驱魔人的前面。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·战斗开始时，特大幅降低敌方全体的\\n\u3000生命值吸收。\\n·怨恨幽灵的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319304, '怨恨幽灵', 319303, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】怨憎之力引发了\\n灵异现象。将从四面八方袭来的诅咒击落，\\n并反击敌人吧！\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·战斗开始时，特大幅降低敌方全体的\\n\u3000生命值吸收。\\n·怨恨幽灵的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。\\n·击退敌方全体，召唤咒怨的墓碑、\\n\u3000咒火的蜡烛和咒毒的菜刀。\\n·咒怨的墓碑经过一定时间后会随机对一名敌人\\n\u3000造成物理伤害（大），并在赋予其诅咒状态后消亡。\\n\u3000但是，若咒怨的墓碑的生命值降至一定值以下，\\n\u3000则会对怨恨幽灵造成一定比例的物理伤害，\\n\u3000并在赋予其诅咒状态后消亡。\\n·咒火的蜡烛经过一定时间后会随机对一名敌人\\n\u3000造成物理伤害（大），并在赋予其烧伤状态后消亡。\\n\u3000但是，若咒火的蜡烛的生命值降至一定值以下，\\n\u3000则会对怨恨幽灵造成一定比例的物理伤害，\\n\u3000并在赋予其烧伤状态后消亡。\\n·咒毒的菜刀经过一定时间后会随机对一名敌人\\n\u3000造成物理伤害（大），并在赋予其中毒状态后消亡。\\n\u3000但是，咒毒的菜刀生命值降至一定值以下，\\n\u3000则会对怨恨幽灵造成一定比例的物理伤害，\\n\u3000并在赋予其中毒状态后消亡。\\n·咒怨的墓碑、咒火的蜡烛和咒毒的菜刀\\n\u3000对角色所造成的伤害\\n\u3000不会回复技能值。');
insert into unit_enemy_data values (319305, '怨恨幽灵', 319303, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】以极度的憎恶和愤怒\\n发出凄厉吼声的恶灵。打破剧烈的诅咒，用尽全力做最后的除灵，\\n驱除怨气冲天的恶灵吧！\\n\\n·战斗开始时，中幅提升自身的物理防御力和\\n\u3000魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·战斗开始时，特大幅降低敌方全体的\\n\u3000生命值吸收。\\n·怨恨幽灵的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会对敌方全体\\n\u3000造成魔法伤害（特大）。同时大幅降低其物理攻击力和\\n\u3000魔法攻击力，\\n\u3000并在一定时间内特大幅降低\\n\u3000生命值回复量。该技能所造成的伤害\\n\u3000不会使角色的技能值回复。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。\\n·以自身为中心，对一定范围内所有敌人造成魔法伤害（大），\\n\u3000并赋予诅咒状态。\\n\u3000当角色的生命值低于80％时，\\n\u3000使其眩晕。该技能所造成的诅咒伤害\\n\u3000无法回复技能值。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（特大）。若角色的生命值低于\\n\u300080%，使其陷入混乱状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·当自身的生命值低于50%时，会展开\\n\u3000更为激烈凶暴的攻击。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319400, '究极守护者', 319400, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】取回了完整力量的上古人工生命体。\\n以无限动力筑起的防御墙，能化解\\n一切攻击。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少50%\\n·究极守护者的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会根据角色的最大生命值，\\n\u3000对敌方全体造成一定比例的魔法伤害，并中幅降低\\n\u3000其物理攻击力和魔法攻击力，\\n\u3000大幅降低其技能值\\n\u3000同时解除属性提升效果。\\n\u3000该技能所造成的伤害\\n\u3000不会使角色的技能值回复。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·以最近一名敌人为中心，对小范围内的敌人\\n\u3000造成物理伤害（大）。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并赋予沉默状态。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·当自身的生命值高于50%时，大幅降低自身的\\n\u3000物理防御力，并展开一个使\\n\u3000物理伤害无效化的屏障。\\n·当自身的生命值低于50%时，大幅降低自身的\\n\u3000魔法防御力，并展开一个使\\n\u3000魔法伤害无效化的屏障。\\n·特大幅提升自身的物理攻击力和\\n\u3000魔法攻击力。若自身正展开屏障，\\n\u3000特大幅回复技能值，\\n>\u3000若自身并未展开屏障，\\n>\u3000则特大幅降低技能值。');
insert into unit_enemy_data values (319500, '解放的暴魔·艾尔基斯', 319500, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】从800年的封印中觉醒的\\n独眼魔物。解开被封印的魔力，顶着令人憎恶的魔术师的名头\\n肆意破坏学园。\\n\\n·必杀技能会解除最近一名敌人\\n\u3000的屏障，并造成固定魔法伤害。同时特大幅提升\\n\u3000自身的魔法攻击力。攻击力的提升量\\n\u3000会根据此技能所造成的伤害量进行提升。\\n·对敌方全体造成魔法伤害（特大）。\\n·对敌方全体造成魔法伤害（大）。\\n·中幅提升自身的魔法攻击力，\\n\u3000中幅回复技能值。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
