insert into unit_enemy_data values (319502, '解放的暴魔·艾尔基斯', 319500, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【魔法】从800年的封印中觉醒的\\n独眼魔物。解开被封印的魔力，顶着令人憎恶的魔术师的名头\\n肆意破坏学园。\\n\\n·必杀技能会解除最近一名敌人\\n\u3000的屏障，并造成固定魔法伤害。同时特大幅提升\\n\u3000自身的魔法攻击力。攻击力的提升量\\n\u3000会根据此技能所造成的伤害量进行提升。\\n·对敌方全体造成魔法伤害（特大）。\\n·对敌方全体造成魔法伤害（大）。\\n·中幅提升自身的魔法攻击力，\\n\u3000中幅回复技能值。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319503, '解放的暴魔·艾尔基斯', 319503, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式1】从四只凶恶的手中放出\\n多彩的魔法球风暴。只要被这炽烈的攻击打中一下，\\n无论任何人都无法再站起来。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少25%\\n·解放的暴魔·艾尔基斯的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除最近一名敌人\\n\u3000的屏障，并造成固定魔法伤害。同时特大幅提升\\n\u3000自身的魔法攻击力。攻击力的提升量\\n\u3000会根据此技能所造成的伤害量进行提升。如果这个技能击倒了敌人，\\n\u3000会特大幅提升自身的魔法攻击力。\\n·对敌方全体造成魔法伤害（中），\\n\u3000并对最近的一名敌人赋予冻结和烧伤状态。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·对敌方全体造成魔法伤害（中），\\n\u3000同时将其击退并使其眩晕。这个技能的异常状态\\n\u3000在伤害免疫的情况下也会成功赋予。');
insert into unit_enemy_data values (319504, '解放的暴魔·艾尔基斯', 319503, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式2】笼罩而来的瘴气\\n幻化成同学的样子。绝不能让这毒辣的丑行玷污\\n灿烂的回忆——\\n\\n·战斗开始时，小幅提升自身的物理防御力\\n\u3000和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少25%\\n·解放的暴魔·艾尔基斯的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319505, '解放的暴魔·艾尔基斯', 319503, 0, 16, 270, 3000, 2, 1.6, 0, 0, 0, '【模式3】暴涨至极限的魔法，\\n现在将全面释放。将学园内人们的声援化作力量，\\n用青春的魔法将它打飞吧。\\n\\n·战斗开始时，小幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身降低技能值的负面状态，\\n\u3000其效果量减少25%\\n·解放的暴魔·艾尔基斯的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n·必杀技能会解除最近一名敌人\\n\u3000的屏障，并造成固定魔法伤害。同时特大幅提升\\n\u3000自身的魔法攻击力。攻击力的提升量\\n\u3000会根据此技能所造成的伤害量进行提升。如果这个技能击倒了敌人，\\n\u3000会特大幅提升自身的魔法攻击力。\\n·对敌方全体造成魔法伤害（特大）。\\n·对敌方全体造成魔法伤害（大）。\\n·中幅提升自身的魔法攻击力，\\n\u3000中幅回复技能值。\\n·对最近的一名敌人造成\\n\u3000魔法伤害（大）。');
insert into unit_enemy_data values (319700, '吹雪', 319700, 0, 16, 270, 3000, 1, 1.6, 0, 0, 0, '【物理】觊觎皇家装备的天才发明家的三女。\\n用浮游兵器射出枪林弹雨和特大炮弹，\\n在空中嘲笑敌人们慌乱躲避的窘态。\\n\\n·战斗开始时，极大幅提升自身的\\n\u3000物理防御力和魔法防御力。通过本防御提升效果\\n\u3000所增加的防御力，不受战斗中\\n\u3000防御力降低技能的影响。\\n·战斗开始时，针对自身受到的技能值降低效果，\\n\u3000其效果量减少100%。\\n·吹雪的所有伤害\\n\u3000都会中幅降低队伍的\\n\u3000技能值回复量。\\n若吹雪的生命值降至一定值以下，浮游兵器便会坠落，\\n\u3000不能行动。在其不能行动期间，自身的\\n\u3000物理防御力和魔法防御力降至0。\\n·必杀技能会解除敌方全体的屏障，\\n\u3000对敌方全体造成物理伤害（特大），\\n\u3000并使其眩晕。这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·普通攻击会对最近的一名角色造成物理伤害（大），\\n\u3000并使其眩晕。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。\\n·击退敌方全体，\\n\u3000并召唤小吹雪的炸药♡。\\n·小吹雪的炸药♡经过一定时间后会对敌方全体\\n\u3000造成物理伤害（大），并在赋予其眩晕状态后消亡。\\n\u3000这个技能的异常状态在伤害免疫的情况下\\n\u3000也会成功赋予。但当小吹雪的炸药♡的生命值降至一定值以下时，\\n\u3000便会对吹雪造成固定的物理伤害，\\n\u3000随后消亡。');
