insert into kmk_navi_comment values (24, 24015, 'カオリ（サマー）', 4, 0.0, 'あうぅ～残念さ～\\nマジムンに魂を\\n奪われちゃったかな～？', 4, 0.0, 10021, 1, 0.0, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24016, 'カオリ（サマー）', 4, 0.0, '今日は踊りのキレも悪いよ～\\nゴーヤーっぽいもの食べて、元気回復さ～！', 1, 0.0, 10021, 2, 4.0, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24017, 'カオリ（サマー）', 4, 0.0, 'マジムンだらけで目が回るさ～！\\nふらふらさ～！', 4, 0.0, 10021, 3, 0.0, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24018, 'カオリ（サマー）', 1, 0.0, 'なんくるない、なんくるない\\n次、頑張ればいいんだよ～！\\n海で泳いで\\nリフレッシュするさー！', 2, 0.0, 10021, 4, 5.2, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24019, 'カオリ（サマー）', 5, 0.0, '恥ずかしいところ\\n見られちゃったね～\\n水着？\u3000水着のことじゃないよー？', 6, 0.0, 10021, 5, 4.2, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24020, 'カオリ（サマー）', 2, 0.0, '絶好調さ～！\u3000この調子で\\n海の平和を取り戻すよ～！\\n楽しい夏が待ってるさ～！', 2, 0.0, 10021, 6, 0.0, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24021, 'カオリ（サマー）', 1, 0.0, '何匹で襲ってきたって\\n私はマブイを落っことさないよ～！\\nくしゅん！\u3000あ……\\n今のくしゃみで落っこちたかも～？', 6, 0.0, 10021, 7, 6.4, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24022, 'カオリ（サマー）', 2, 0.0, 'ひーやーさーさー♪\\n戦いの後は、踊りを踊って\\n気分スッキリさ～！', 2, 0.0, 10021, 8, 0.0, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24023, 'カオリ（サマー）', 6, 0.0, 'あれ～？\u3000戦ってるうちに\\n水着がどっか行っちゃった～？\\nねぇキミ、どっかで見なかった～？\\nなんで離れるさ～？', 3, 0.0, 10021, 9, 10.08, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24024, 'カオリ（サマー）', 1, 0.0, 'まだまだ私は元気だよ～！\\nマジムンでもなんでも、\\nどんとこーいさー♪', 2, 0.0, 10021, 10, 5.0, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
insert into kmk_navi_comment values (24, 24025, 'カオリ（サマー）', 2, 0.0, '私の空手は世界最強さ～！\\n夏の陽射しと青い海も合わされば、\\n向かうところ敵なしだよー！', 2, 0.0, 10021, 11, 0.0, 110501, '2019/07/31 12:00:00', '2030/12/17 14:59:59');
