insert into login_bonus_message_data values ('カリン', 2, 0, 0, '', 2, 190801, 1, 80003, 0, 135, 'そうなのですか？\\nキャルさんのことですからてっきり\\n2.5周年をどう盛り上げたら\\nいいか悩んでいるのかと…');
insert into login_bonus_message_data values ('キャル', 7, 0, 0, '', 2, 106001, 0, 80003, 0, 136, '分かってんじゃないのよ！？\\nいっ、いや、違うけどね！\\nあたしは別にそんなことで\\n悩んでないわよっ？');
insert into login_bonus_message_data values ('カリン', 3, 0, 0, '', 2, 190801, 1, 80003, 0, 137, 'ふふっ…\\nこういう時は、真心を込めて\\n気持ちをそのまま口にしたら\\nいいのではないでしょうか？');
