insert into login_bonus_message_data values ('コッコロ', 6, 0, 0, '', 3, 105901, 0, 80003, 0, 144, 'ペコリーヌさま、もうすぐ\\n記念日でございますね\\nこういった節目というのは\\n感慨深いものでございます');
