insert into login_bonus_message_data values ('ペコリーヌ', 11, 0, 0, '', 4, 105801, 0, 80003, 0, 156, '2.5周年カウントダウン♪\\nキャルちゃんキャルちゃん！\\nわくわくしますね☆');
