insert into pkb_navi_comment values ('ミサト', 0.0, '2030/12/17 14:59:59', 6, '2020/07/31 12:00:00', 'あら～…もしかして\\n勝っちゃったのかしら？\\nみんなの練習を見ていたから\\nかもしれないわね', 10046, 2, 24, 24013, 0.0, 5.33, 113501, 111);
