insert into pkb_navi_comment values ('ダイチ', 0.0, '2030/12/17 14:59:59', 1, '2020/07/31 12:00:00', 'まだ練習が足りねえみてえだな\\nこれっぽっちも本気で\\n投げてねえっつーの\\nもう一回やんぞ！', 10046, 2, 24, 24016, 0.0, 6.11, 192401, 611);
insert into pkb_navi_comment values ('ダイチ', 0.0, '2030/12/17 14:59:59', 1, '2020/07/31 12:00:00', 'ふん、当然の結果だな\\n俺の球は、誰にも打てねえよ！\\nまたやりてえっつーなら\\nとことん付き合ってやるぜ！', 10046, 2, 24, 24017, 0.0, 6.75, 192401, 612);
insert into pkb_navi_comment values ('ダイチ', 0.0, '2030/12/17 14:59:59', 2, '2020/07/31 12:00:00', 'おっしゃあ！\\nこの勢いで全国に行ってやるぜ！\\n全員三振で仕留めてやる！', 10046, 2, 24, 24018, 0.0, 0.0, 192401, 613);
insert into pkb_navi_comment values ('ハツネ', 0.0, '2030/12/17 14:59:59', 2, '2020/07/31 12:00:00', 'きらーん☆\\nハツネちゃんの大勝利なのだ～！\\nみんながもっと上達できるように\\n私もまだまだ頑張るからね！', 10046, 1, 24, 24019, 0.0, 4.52, 113401, 211);
insert into pkb_navi_comment values ('ハツネ', 0.0, '2030/12/17 14:59:59', 2, '2020/07/31 12:00:00', 'やったあ！\u3000私の勝ちだね！\\nチアリーダーもピッチャーも\\nハツネちゃんに任せてよ！', 10046, 1, 24, 24020, 0.0, 3.89, 113401, 212);
