insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 192201, 1, 'アサヒ', 0.0, 701, '特訓の成果が出たみたいだ！\\nもっと練習を重ねて\\nこの調子で上達していこう！', 2, 1.99, 24004, 0.0);
