insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 113501, 2, 'ミサト', 0.0, 111, 'あら～…もしかして\\n勝っちゃったのかしら？\\nみんなの練習を見ていたから\\nかもしれないわね', 6, 5.33, 24013, 0.0);
insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 113501, 2, 'ミサト', 0.0, 112, 'みんなの力になりたくて\\n先生も頑張ってみました～！\\nうまく投げられたみたいで\\nよかったわ～！', 1, 6.85, 24014, 0.0);
insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 113501, 2, 'ミサト', 0.0, 113, '練習お疲れさま～！\\n先生も協力できて\\nとってもうれしいわ～♪\\n次も頑張りましょうね～！', 1, 3.0, 24015, 0.0);
insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 192401, 2, 'ダイチ', 0.0, 611, 'まだ練習が足りねえみてえだな\\nこれっぽっちも本気で\\n投げてねえっつーの\\nもう一回やんぞ！', 1, 6.11, 24016, 0.0);
insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 192401, 2, 'ダイチ', 0.0, 612, 'ふん、当然の結果だな\\n俺の球は、誰にも打てねえよ！\\nまたやりてえっつーなら\\nとことん付き合ってやるぜ！', 1, 6.75, 24017, 0.0);
insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 192401, 2, 'ダイチ', 0.0, 613, 'おっしゃあ！\\nこの勢いで全国に行ってやるぜ！\\n全員三振で仕留めてやる！', 2, 0.0, 24018, 0.0);
insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 113401, 1, 'ハツネ', 0.0, 211, 'きらーん☆\\nハツネちゃんの大勝利なのだ～！\\nみんながもっと上達できるように\\n私もまだまだ頑張るからね！', 2, 4.52, 24019, 0.0);
insert into pkb_navi_comment values (10046, 24, '2030/12/17 14:59:59', '2020/07/31 12:00:00', 113401, 1, 'ハツネ', 0.0, 212, 'やったあ！\u3000私の勝ちだね！\\nチアリーダーもピッチャーも\\nハツネちゃんに任せてよ！', 2, 3.89, 24020, 0.0);
