insert into srt_top_talk values (1, 2, 10001, 'vo_minigame_1004_top_1_000', 'vo_minigame_1004', 1000101, 'ふっふっふ、これでカヤぴぃとボスを見返してやるです！');
insert into srt_top_talk values (1, 2, 10002, 'vo_minigame_1004_top_1_003', 'vo_minigame_1004', 1000201, 'しりとりでの戦い、お兄さんにも手伝ってもらうです');
insert into srt_top_talk values (2, 1, 10003, 'vo_minigame_1004_top_1_004', 'vo_minigame_1004', 1000301, 'どんなケンカも売られた以上は買うのが喧嘩屋だ！やってやるぜ！');
insert into srt_top_talk values (2, 1, 10004, 'vo_minigame_1004_top_1_005', 'vo_minigame_1004', 1000401, '石板でしりとりか……イノリのヤツおもしろいこと考えるな');
insert into srt_top_talk values (2, 1, 10005, 'vo_minigame_1004_top_1_006', 'vo_minigame_1004', 1000501, 'しりとりだろうがなんだろうが返り討ちにしてやるぜ');
insert into srt_top_talk values (3, 2, 10006, 'vo_minigame_1004_top_1_007', 'vo_minigame_1004', 1000601, 'ぐもも……（オデ、ハラヘッタ）');
insert into srt_top_talk values (3, 2, 10007, 'vo_minigame_1004_top_1_008', 'vo_minigame_1004', 1000701, 'ぐもももも（シリトリ タノシイ）');
insert into srt_top_talk values (3, 2, 10008, 'vo_minigame_1004_top_1_009', 'vo_minigame_1004', 1000801, 'ぐもももも！（イノリ イイヤツ）');
insert into srt_top_talk values (1, 2, 10009, 'vo_minigame_1004_top_2_010', 'vo_minigame_1004', 1000901, 'カヤぴぃ相手でも手加減しないです');
insert into srt_top_talk values (2, 1, 10009, 'vo_minigame_1004_top_2_011', 'vo_minigame_1004', 1000902, '上等だ！やるからには本気で来いよな');
insert into srt_top_talk values (1, 2, 10010, 'vo_minigame_1004_top_2_012', 'vo_minigame_1004', 1001001, 'カヤぴぃ、謝るなら今のうちです');
insert into srt_top_talk values (2, 1, 10010, 'vo_minigame_1004_top_2_013', 'vo_minigame_1004', 1001002, 'イノリこそ泣きを入れるなら今のうちだぜ？');
insert into srt_top_talk values (1, 2, 10011, 'vo_minigame_1004_top_2_014', 'vo_minigame_1004', 1001101, 'カヤぴぃ、これからは知性の時代ですよ？');
insert into srt_top_talk values (2, 1, 10011, 'vo_minigame_1004_top_2_015', 'vo_minigame_1004', 1001102, '待て！オレがバカみたいにいうな！！');
insert into srt_top_talk values (1, 1, 10012, 'vo_minigame_1004_top_2_016', 'vo_minigame_1004', 1001201, 'さあ、あたしたちの力を見せてやるです');
insert into srt_top_talk values (3, 2, 10012, 'vo_minigame_1004_top_2_017', 'vo_minigame_1004', 1001202, 'ぐも！（マカセロ！）');
insert into srt_top_talk values (1, 1, 10013, 'vo_minigame_1004_top_2_018', 'vo_minigame_1004', 1001301, 'よしよし、いいですようまくできたらチョコをあげるです！');
insert into srt_top_talk values (3, 2, 10013, 'vo_minigame_1004_top_2_019', 'vo_minigame_1004', 1001302, 'ぐも！（オデ チョコスキ）');
insert into srt_top_talk values (2, 1, 10014, 'vo_minigame_1004_top_2_023', 'vo_minigame_1004', 1001401, 'なあイノリ……もしかして当初の目的見失ってないよな？');
insert into srt_top_talk values (1, 2, 10014, 'vo_minigame_1004_top_2_024', 'vo_minigame_1004', 1001402, 'もちろん覚えてるですカヤぴぃを倒し我々の知能を証明するです');
insert into srt_top_talk values (2, 1, 10015, 'vo_minigame_1004_top_2_025', 'vo_minigame_1004', 1001501, 'いくらイノリ相手でも勝ちを譲る気はないからな！');
insert into srt_top_talk values (1, 2, 10015, 'vo_minigame_1004_top_2_026', 'vo_minigame_1004', 1001502, '望むところです手加減はなしですよ');
insert into srt_top_talk values (2, 1, 10016, 'vo_minigame_1004_top_2_027', 'vo_minigame_1004', 1001601, 'へっ、なかなかやるじゃねえか');
insert into srt_top_talk values (1, 2, 10016, 'vo_minigame_1004_top_2_028', 'vo_minigame_1004', 1001602, 'カヤぴぃも思ったよりやるですね！');
insert into srt_top_talk values (2, 1, 10017, 'vo_minigame_1004_top_2_029', 'vo_minigame_1004', 1001701, 'お～い、ドラゴンもどき？');
insert into srt_top_talk values (3, 2, 10017, 'vo_minigame_1004_top_2_030', 'vo_minigame_1004', 1001702, 'ぐもっ！？（ヒッ！）');
insert into srt_top_talk values (2, 1, 10018, 'vo_minigame_1004_top_2_031', 'vo_minigame_1004', 1001801, 'いやあ、あんときは悪かったなはっはっは');
insert into srt_top_talk values (3, 2, 10018, 'vo_minigame_1004_top_2_032', 'vo_minigame_1004', 1001802, 'ぐももも……（かやピィ コワカッタ）');
insert into srt_top_talk values (2, 1, 10019, 'vo_minigame_1004_top_2_033', 'vo_minigame_1004', 1001901, 'お前ってさ……ほんとマヌケな顔してるよな');
insert into srt_top_talk values (3, 2, 10019, 'vo_minigame_1004_top_2_034', 'vo_minigame_1004', 1001902, 'ぐも……（オマエガ イウカ）');
insert into srt_top_talk values (3, 2, 10020, 'vo_minigame_1004_top_2_044', 'vo_minigame_1004', 1002001, 'ぐも……（ハラヘッタ チョコホシイ）');
insert into srt_top_talk values (2, 1, 10020, 'vo_minigame_1004_top_2_045', 'vo_minigame_1004', 1002002, 'なんだ？眠いのか？');
insert into srt_top_talk values (3, 2, 10021, 'vo_minigame_1004_top_2_046', 'vo_minigame_1004', 1002101, 'ぐもも（コイツ……キケン）');
insert into srt_top_talk values (2, 1, 10021, 'vo_minigame_1004_top_2_047', 'vo_minigame_1004', 1002102, 'うん？\u3000なんだ？オレの舎弟にでもなりたいのか？');
insert into srt_top_talk values (3, 2, 10022, 'vo_minigame_1004_top_2_048', 'vo_minigame_1004', 1002201, 'ぐも…（かやピィノウキン）');
insert into srt_top_talk values (2, 1, 10022, 'vo_minigame_1004_top_2_049', 'vo_minigame_1004', 1002202, 'おい、今失礼なこと考えてるだろ？');
insert into srt_top_talk values (2, 1, 10023, 'vo_minigame_1004_top_3_058', 'vo_minigame_1004', 1002301, 'なあ、これいつまで続けるんだ？');
insert into srt_top_talk values (1, 2, 10023, 'vo_minigame_1004_top_3_059', 'vo_minigame_1004', 1002302, 'カヤぴぃが降参するまでです！');
insert into srt_top_talk values (3, 2, 10023, 'vo_minigame_1004_top_3_060', 'vo_minigame_1004', 1002303, 'ぐもも！（コウサンシロ！）');
insert into srt_top_talk values (3, 2, 10024, 'vo_minigame_1004_top_3_065', 'vo_minigame_1004', 1002401, 'ぐも！（イノリ チョコホシイ）');
insert into srt_top_talk values (1, 1, 10024, 'vo_minigame_1004_top_3_066', 'vo_minigame_1004', 1002402, 'しょうがないですね、食べ過ぎはダメですよ');
insert into srt_top_talk values (2, 1, 10024, 'vo_minigame_1004_top_3_067', 'vo_minigame_1004', 1002403, '土産のチョコ、そうとう気に入ったみたいだな');
insert into srt_top_talk values (3, 2, 10025, 'vo_minigame_1004_top_3_068', 'vo_minigame_1004', 1002501, 'ぐも！（かやピィニハ マケナイ！）');
insert into srt_top_talk values (2, 1, 10025, 'vo_minigame_1004_top_3_069', 'vo_minigame_1004', 1002502, 'お？なんだ？腹でも減ってるのか？');
insert into srt_top_talk values (1, 2, 10025, 'vo_minigame_1004_top_3_070', 'vo_minigame_1004', 1002503, '……全然話が通じてないです');
