insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 213900, 1, 1, 650, 0, 0, 'ギャングオッター', 3.5, 213900, '【物理】中距離攻撃\\n貝殻をぶつけて不意をつく、\\n意地悪な北海のチンピラっこ。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 214000, 0, 2, 800, 0, 0, 'マジックレイ', 3.5, 214000, '【魔法】範囲攻撃\\n頭部の石から発する超音波で\\n巧みに狩りをするエイの魔物。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 214100, 15, 1, 200, 0, 0, 'スタンプ', 2.5, 214100, '【物理】前衛・近接攻撃\\nしなやかな枝を、ムチのように\\n使い攻撃する木の魔物。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 214200, 15, 1, 400, 0, 0, 'ポイズンポッド', 2.5, 214200, '【物理】近接攻撃\\n大きく開いた口に溜めた\\n溶解液で獲物を食らう。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 214300, 15, 2, 800, 0, 0, 'ニンフ', 2.5, 214300, '【魔法】中距離攻撃\\n遊び相手を見つけては、見境なしに\\nイタズラをする、森の精霊。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 214400, 0, 2, 645, 0, 0, 'スリーピィオウル', 3.5, 214400, '【魔法】中距離攻撃\\n寝不足で気怠げなフクロウの魔物。\\n敵を睨み術をかけ、動きを奪う。', 0, 270);
