insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 214901, 2, 1, 240, 0, 0, 'ムイミのシャドウ', 1.8, 214901, 'ムイミの姿をした特殊な魔物\\n\\n・目の前の敵１キャラに物理特大ダメージを与え\\n\u3000る。さらにＴＰが無くなるまでの間天楼覇断剣\\n\u3000を装備する。\\n・前方範囲内の敵すべてに物理小ダメージを与え、\\n\u3000さらにスタンさせて小さく吹き飛ばす。\\n・前方範囲内の敵すべての物理防御力を小ダウン\\n\u3000させる。', 0, 450);
