insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 216501, 3, 1, 464, 0, 0, 'リリ（フォールン）のシャドウ', 2.09, 216501, 'リリ（フォールン）の姿をした特殊な魔物\\n\\n・【召喚】目の前の敵１キャラに物理大ダメージ\\n\u3000を与える。このスキルのダメージは自分のシャ\\n\u3000ドウの数に応じて増加する。さらにユニオン\\n\u3000バーストを使用しない、自分のシャドウを２体\\n\u3000生成する。\\n\u3000生成されたシャドウは自分を飛行状態にする。\\n・自分を中心とした範囲内の味方すべての物理攻\\n\u3000撃力を特大アップさせる。\\n・目の前の敵１キャラに物理大ダメージを与え、\\n\u3000物理防御力を小ダウンさせる。', 0, 450);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 216601, 0, 1, 406, 0, 0, 'クリア（フォールン）のシャドウ', 2.09, 216601, 'クリア（フォールン）の姿をした特殊な魔物\\n\\n・目の前の敵１キャラに物理中ダメージを与え、\\n\u3000自分のＨＰを中回復させる。自分を除く味方の\\n\u3000残りＨＰに応じて、このスキルのダメージが\\n\u3000アップする。\\n・目の前の敵１キャラに物理中ダメージを与え、\\n\u3000物理防御力を中ダウンさせる。自分を除く味方\\n\u3000の残りＨＰに応じて、このスキルのダメージが\\n\u3000アップする。\\n・自分以外の最もＨＰの低い味方１キャラに継続\\n\u3000ＨＰ回復状態と【堕天使の加護】を付与する。\\n\u3000【堕天使の加護】が付与されている間、その\\n\u3000キャラへの敵の攻撃をすべて代わりに受け、自\\n\u3000分への物理ダメージと魔法ダメージをカットし、\\n\u3000自分が敵から受ける行動不能を無効化する。', 0, 450);
