insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 301300, 4, 1, 1000, 0, 0, 'グラットン', 3.0, 301300, '暴食の凶獣として悪名轟く、天秤座の魔星獣。\\n奪った魂の善悪をはかり、味の違いを愉しむ。\\n\\n・敵からの攻撃による麻痺、スタン、束縛、\\n\u3000行動速度低下効果への耐性を持たない。\\n・敵全体に魔法大ダメージを与え、さらに自\\n\u3000身の物理攻撃力と魔法攻撃力を特大アップ。\\n・後方範囲内の敵に魔法中ダメージ。\\n・前方範囲内の敵に魔法中ダメージを与え、Ｔ\\n\u3000Ｐを小ダウン。さらに自身のＴＰを中アップ\\n\u3000させる。', 0, 270);
