insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 302100, 11, 1, 3000, 0, 0, 'ワイバーン', 1.6, 302100, '【物理】大きな皮翼を使い、空を駆ける翼竜。\\n巨大な火球を吐き、侵入者の行く手を阻む。\\n\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を小アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・ワイバーンから受けるすべてのダメージで発生\\n\u3000する、パーティキャラのＴＰ回復量を中ダウン\\n\u3000させる。\\n・ユニオンバーストは、最も近い敵１キャラを中\\n\u3000心にした広範囲内の敵最大３キャラに物理大ダ\\n\u3000メージを与える。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 302101, 11, 1, 3000, 0, 0, 'ワイバーン', 1.6, 302101, '【物理】大きな皮翼を使い、空を駆ける翼竜。\\n巨大な火球を吐き、侵入者の行く手を阻む。\\n\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を中アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・バトル開始時、敵全体のＨＰ吸収を特大ダウン\\n\u3000させ、ＴＰ上昇を小ダウンさせる。\\n・バトル開始時、自身にＴＰダウン50％カット\\n\u3000効果を付与する。\\n・ワイバーンから受けるすべてのダメージで発生\\n\u3000する、パーティキャラのＴＰ回復量を特大ダウ\\n\u3000ンさせる。\\n・ユニオンバーストは、最も近い敵１キャラを中\\n\u3000心にした広範囲内の敵最大３キャラに物理大ダ\\n\u3000メージを与え、物理防御力を中ダウンさせる。\\n\u3000さらに自身の物理防御力をバトル終了まで小\\n\u3000アップさせる。このスキルの状態異常はダメー\\n\u3000ジを無効化しても付与される。\\n・最も近い敵３キャラをノックバックさせ、行動\\n\u3000速度を中ダウンさせる。', 0, 270);
