insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 303501, 13, 1, 1000, 0, 0, 'ムシュフシュ', 1.8, 303501, '【物理】吸い続けた他者の魔力で妖術を得た魔物。\\n敵に付けた傷から魔力を奪いさらなる力を求める。\\n\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を小アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・ムシュフシュから受けるすべてのダメージで発\\n\u3000生する、パーティキャラのＴＰ回復量を小ダウ\\n\u3000ンさせる。\\n・ユニオンバーストは、最も近い敵１キャラを中\\n\u3000心にした小範囲に毒状態を付与する。さらに敵\\n\u3000全体に物理攻撃力中ダウンと魔法攻撃力大ダウ\\n\u3000ンを付与する。このスキルにより付与された毒\\n\u3000のダメージでは、ＴＰが回復しない。\\n・最も近い敵１キャラを中心にした小範囲に物理\\n\u3000大ダメージを与える。', 0, 270);
