insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 305102, 16, 1, 1000, 0, 0, 'メガラパーン', 1.8, 305102, '【物理】巨体を駆使して暴れ回る大ウサギの魔物。\\n繰り出す一撃は消耗が大きく、持てあまし気味。\\n\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を小アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・メガラパーンから受けるすべてのダメージで発\\n\u3000生する、パーティキャラのＴＰ回復量を中ダウ\\n\u3000ンさせる。\\n・ユニオンバーストは、敵全体に物理中ダメージ\\n\u3000を与え、ノックバックさせる。さらにキャラの\\n\u3000ＨＰが80％未満の場合、スタン状態を付与す\\n\u3000る。このスキルの状態異常はダメージを無効化\\n\u3000しても付与される。このスキルによるダメージ\\n\u3000では、ＴＰが回復しない。\\n・敵全体に物理小ダメージを与えて吹き飛ばす。\\n\u3000その後、自身のＴＰを小回復。\\n・通常攻撃は最も近い敵１キャラに物理大ダメー\\n\u3000ジを与え、さらにノックバックさせる。', 0, 270);
