insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 306700, 16, 1, 3000, 0, 0, 'アグリーラブリー', 1.8, 306705, '【MODE2】カカオウラは奮起し、\\n 死地に飛び込む。\\n 激化する魔物の攻撃から、\\n 大切な者たちを守るため。\\n\\n・ユニオンバーストは、敵全体にキャラの\\n\u3000最大ＨＰに応じた物理大ダメージを与え、\\n・カカオウラがキャラの味方として出現する。\\n\u3000カカオウラ出現中は、キャラ全体の物理\\n\u3000攻撃力と魔法攻撃力と行動速度が中アップする。\\n・敵全体に魔法小ダメージを与え、\\n\u3000行動速度を大ダウンさせる。\\n', 0, 270);
