insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308000, 16, 1, 3000, 0, 0, 'マジムン', 1.6, 308010, '【ＭＯＤＥ２】宵闇より現れし怨霊の叢りは、\\n人々の魂を千々に引き裂く。\\n\\n・分身体を１体倒すたびに、残りマジムン数が\\n\u3000１減少する。その後、分身体が再度召喚される。\\n・マジムンが保持している【吸魂】ステータス\\n\u3000の数に応じて、通常攻撃と各スキルの効果\\n\u3000が強化される。\\n・通常攻撃のたびに【吸魂】を６つ追加する。\\n・攻撃するたびにマジムンに【吸魂】を１つ\\n\u3000追加する分身体を３体召喚し、さらに敵全体を\\n\u3000小さく吹き飛ばす。\\n・マジムンがクリティカルダメージを受ける\\n\u3000たびに、マジムンの【吸魂】が２つ減少する。\\n・分身体がクリティカルダメージを受ける\\n\u3000たびに、マジムンの【吸魂】が１つ減少する。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308000, 16, 1, 3000, 0, 0, 'マジムン', 1.6, 308011, '【ＭＯＤＥ３】暁闇の中で跋扈する魑魅は、\\n遂には三千世界の魂を喰らわんと牙を剥く。\\n\\n・マジムンが保持している【吸魂】ステータス\\n\u3000の数に応じて、通常攻撃と各スキルの効果\\n\u3000が強化される。\\n・通常攻撃のたびに【吸魂】を６つ追加する。\\n・ユニオンバーストは、敵全体にマジムンが\\n\u3000保持している【吸魂】の数に応じた\\n\u3000魔法割合ダメージを与え、\\n\u3000さらに【吸魂】を５つ追加する。\\n・敵全体に魔法小ダメージを与えＴＰを減少させ、\\n\u3000さらにＴＰ上昇をダウンさせるフィールドを\\n\u3000展開する。ＴＰの減少量とフィールドの\\n\u3000効果値は、マジムンが保持している【吸魂】\\n\u3000の数が多い程強化される。\\n・攻撃するたびにマジムンに【吸魂】を１つ\\n\u3000追加する分身体を３体召喚し、さらに敵全体を\\n\u3000小さく吹き飛ばす。\\n・マジムンがクリティカルダメージを受ける\\n\u3000たびに、マジムンの【吸魂】が２つ減少する。\\n・分身体がクリティカルダメージを受ける\\n\u3000たびに、マジムンの【吸魂】が１つ減少する。\\n・残り時間が30秒を切ると、さらに\\n\u3000攻撃が激しく狂暴になる。\\n\u3000さらに【吸魂】を20追加する。', 0, 270);
