insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308200, 16, 1, 3000, 0, 0, 'オオドクマンドラゴラ', 1.6, 308201, '【物理】異常発達したアオイのマンドラゴラ。\\n強靭な根と種子の弾丸で眼前の敵を圧倒する。\\n\\n・前方２キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージ。\\n・ユニオンバーストは、敵全体に\\n\u3000物理大ダメージを与え、与えた合計ダメー\\n\u3000ジに応じて自身のＨＰを回復させる。\\n・敵全体に連続で物理小ダメージを与えつつ、\\n\u3000物理防御力を３度ダウンさせる。\\n・自身の物理攻撃力をアップさせつつ、ＴＰ\\n\u3000を上昇させる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308200, 16, 1, 3000, 0, 0, 'オオドクマンドラゴラ', 1.6, 308202, '【物理】異常発達したアオイのマンドラゴラ。\\n強靭な根と種子の弾丸で眼前の敵を圧倒する。\\n\\n・前方２キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージ。\\n・ユニオンバーストは、敵全体に\\n\u3000物理大ダメージを与え、与えた合計ダメー\\n\u3000ジに応じて自身のＨＰを回復させる。\\n・敵全体に連続で物理小ダメージを与えつつ、\\n\u3000物理防御力を３度ダウンさせる。\\n・自身の物理攻撃力をアップさせつつ、ＴＰ\\n\u3000を上昇させる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308200, 16, 1, 3000, 0, 0, 'オオドクマンドラゴラ', 1.6, 308203, '【物理】異常発達したアオイのマンドラゴラ。\\n強靭な根と種子の弾丸で眼前の敵を圧倒する。\\n\\n・前方２キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージ。\\n・ユニオンバーストは、敵全体に\\n\u3000物理大ダメージを与え、与えた合計ダメー\\n\u3000ジに応じて自身のＨＰを回復させる。\\n・敵全体に連続で物理小ダメージを与えつつ、\\n\u3000物理防御力を３度ダウンさせる。\\n・自身の物理攻撃力をアップさせつつ、ＴＰ\\n\u3000を上昇させる。\\n・マンドラゴラ自身のＨＰが40％を切ると、\\n\u3000さらに攻撃が激しく狂暴になる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308204, 16, 1, 3000, 0, 0, 'オオドクマンドラゴラ', 1.6, 308204, '【MODE2】猛毒の胞子が降り注ぎ、大地は\\n荒野と化す。\\n数多の骸を苗床に、猛毒の大輪が鮮やかに\\n咲き誇る。\\n\\n下記の行動を順番に繰り返す。\\n・地面に埋まっている間、ダメージを受け付け\\n\u3000ない状態になる。\\n・敵全体を強力な毒状態にする。\\n・自身にかかっているステータスダウン効果を\\n\u3000解除したあと、自身の物理防御力と魔法防御\\n\u3000力を極大ダウン。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308200, 16, 1, 3000, 0, 0, 'オオドクマンドラゴラ', 1.6, 308205, '【MODE1】触手から滴り落ちる猛毒の雫。\\nその一滴は泉を枯らし、森を腐らせ、命を侵す。\\n\\n・前方２キャラに物理ダメージを与え、さらに\\n\u3000毒状態にする。\\n・ユニオンバーストは、敵全体に\\n\u3000物理大ダメージを与え、与えた合計ダメー\\n\u3000ジに応じて自身のＨＰを回復させる。\\n\u3000毒状態のキャラには、ダメージが大アップ。\\n・敵全体に連続で物理小ダメージを与えつつ、\\n\u3000物理防御力を３度ダウンさせる。\\n・自身の物理攻撃力をアップさせつつ、ＴＰ\\n\u3000を上昇させる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308200, 16, 1, 3000, 0, 0, 'オオドクマンドラゴラ', 1.6, 308206, '【MODE3】絶命の咆哮、巨腕の蹂躙。\\n花のように散る命。自らの断末魔を子守歌に、\\n亡骸は永遠の眠りにつく。\\n\\n・前方２キャラに最大ＨＰに応じた物理割合\\n\u3000ダメージ。\\n・ユニオンバーストは、敵全体に\\n\u3000物理大ダメージを与え、与えた合計ダメー\\n\u3000ジに応じて自身のＨＰを回復させる。\\n・敵全体に連続で物理小ダメージを与えつつ、\\n\u3000物理防御力を３度ダウンさせる。\\n・自身の物理攻撃力をアップさせつつ、ＴＰ\\n\u3000を上昇させる。\\n・残り時間が40秒を切ると、さらに攻撃が\\n\u3000激しく狂暴になる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 308207, 16, 1, 1000, 0, 0, 'ラフレシアマンドラゴラ', 1.6, 308207, '【物理】塔の魔力で開花した劇毒を放つ死の大花。\\nその毒は、全ての感覚を狂わせ生ある全てを蝕む。\\n\\n・前方２キャラに物理ダメージを与える。\\n\u3000自身のＨＰが80％を切っている場合、さらに\\n\u3000毒状態にする。\\n・ユニオンバーストは敵全体に物理大ダメージを\\n\u3000与え、毒状態のキャラに割合ダメージを与える。\\n\u3000その後、敵全体の物理攻撃力を大ダウンさせ、\\n\u3000自身の物理攻撃力を大アップさせる。\\n・敵全体に物理中ダメージを与え、物理防御をダ\\n\u3000ウンさせる。\\n・自分の物理攻撃力とＴＰをアップする。\\n・残りＨＰが80％を切ると、さらに攻撃が激し\\n\u3000く狂暴になる。\\n・地面に埋まっている間、ダメージを受け付けな\\n\u3000い状態になり、敵全体に物理中ダメージを与え、\\n\u3000敵全体を長時間、毒状態にする。さらに、毒状\\n\u3000態のキャラを猛毒状態にする。\\n\u3000その後、地面から出てきた際に、物理防御力と\\n\u3000魔法防御力を極大ダウン。', 0, 270);
