insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 309300, 16, 1, 3000, 0, 0, 'トナカイ・ヴェンデッタ', 1.8, 309301, '【物理】主人へ反逆を決めた、エリートトナカイ。\\n欲深いサンタたちへ、従者の心の鉄槌を下す。\\n\\n・ユニオンバーストは、召喚キャラを消滅させた\\n\u3000あと、敵全体にキャラの最大ＨＰに応じた\\n\u3000物理割合ダメージを与える。\\n\u3000このダメージを受けたあとにＨＰが40％を\\n\u3000切ったキャラを一定時間トナカイに変え、\\n\u3000さらに物理クリティカルと魔法クリティカルを\\n\u3000下げる。トナカイになったキャラは、通常攻撃\\n\u3000しか使用できなくなる。\\n\u3000一定時間、自身の物理防御力を大きく下げる。\\n・一番前のキャラを中心として、一定範囲に\\n\u3000キャラの最大ＨＰに応じた物理割合ダメージを\\n\u3000与える。さらに自身の物理攻撃力をアップ\\n\u3000させる。\\n・一番前のキャラに物理ダメージを与えて\\n\u3000ノックバックさせ、さらにスタンさせる。', 0, 270);
