insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310000, 16, 1, 3000, 0, 0, '山神様', 1.6, 310004, '【ＭＯＤＥ１】冒険者の力を試す、内なる影を\\n召喚。寒風吹き荒ぶ天下の険、自身の脆さが\\n試される。\\n\\n・一番前のキャラに物理ダメージを与える。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310000, 16, 1, 3000, 0, 0, '山神様', 1.6, 310005, '【ＭＯＤＥ２】益荒男の疾駆に大地が揺れる。\\nその様を祝賀するが如く、目出鯛は巌山を舞う。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与え、\\n\u3000ノックバックさせる。\\n\u3000先頭のキャラにはさらに追加で物理特大\\n\u3000ダメージを与え、大きく吹き飛ばして\\n\u3000スタンさせる。\\n\u3000山神様がユニオンバーストを発動するたび\\n\u3000自身の物理防御力がダウンする。\\n\u3000この効果は累積する。\\n・魔法攻撃力の最も高い２キャラに、キャラの\\n\u3000最大ＨＰに応じた魔法割合ダメージを与え、\\n\u3000ＴＰを大きくダウンさせる。\\n・後方に物理防御力ダウンフィールドを展開し、\\n\u3000さらに敵全体に物理中ダメージを与える。\\n\u3000目出鯛が背中を離れている間は、山神様の\\n\u3000魔法防御力が特大ダウン。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310000, 16, 1, 3000, 0, 0, '山神様', 1.6, 310006, '【ＭＯＤＥ３】神と競いし強者への、終の試煉。\\n互いの全てを以て、想い人へ駆け抜ける。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与え、\\n\u3000ノックバックさせる。\\n\u3000先頭のキャラにはさらに追加で物理特大\\n\u3000ダメージを与え、大きく吹き飛ばして\\n\u3000スタンさせる。\\n\u3000山神様がユニオンバーストを発動するたび\\n\u3000自身の物理防御力がダウンする。\\n\u3000この効果は累積する。\\n・魔法攻撃力の最も高い１キャラに、キャラの\\n\u3000最大ＨＰに応じた魔法割合ダメージを与え、\\n\u3000ＴＰを大きくダウンさせる。\\n・自身のＴＰと行動速度をアップさせる。\\n・バトル開始直後に、さらに攻撃が激しく\\n\u3000凶暴になる代わりに、自身の物理防御力が\\n\u3000ダウンする。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310100, 14, 1, 1000, 0, 0, 'ラースドラゴン', 2.6, 310100, '【物理】忘却の秘境に、住まう若き巨竜。\\n禁忌に踏み入る冒険者を灼熱の業火で焼き払う。\\n\\n・ユニオンバーストは敵全体に物理割合ダメージ\\n\u3000を与え、さらにＴＰをダウンさせる。\\n・バトル開始時に、自身のＴＰを特大ダウン。\\n・前方1キャラを中心に一定範囲内のキャラに\\n\u3000魔法ダメージを与え、さらに物理防御力を\\n\u3000ダウンさせる。\\n・敵全体に物理ダメージを与え、さらに火傷\\n\u3000状態にする。\\n・敵全体に物理ダメージを与え、ノックバック\\n\u3000させる、さらに、ステータスアップ効果を\\n\u3000打ち消す。\\n\\n・全ターゲットがブレイクされると、自身のＴＰ\\n\u3000を特大ダウン。\\n・ラースドラゴン自身の残りＨＰが10％を切る\\n\u3000とさらに攻撃が激しく強暴になり、ブレイク\\n\u3000されなくなる。\\n・バトルがタイムアップすると、敵全体を戦闘\\n\u3000不能にする。\\n', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310101, 14, 1, 1000, 0, 0, 'ラースドラゴン', 2.6, 310101, '【物理】忘却の秘境に、住まう若き巨竜。\\n禁忌に踏み入る冒険者を灼熱の業火で焼き払う。\\n\\n・バトル開始時、自身の物理防御力と魔法防御力\\n\u3000を小アップさせる。この防御力アップ効果で上\\n\u3000昇した防御力は、バトル中に受ける防御力ダウ\\n\u3000ンスキルの影響を受けない。\\n・ラースドラゴンから受けるすべてのダメージで\\n\u3000発生する、パーティキャラのＴＰ回復量を中ダ\\n\u3000ウンさせる。\\n・ユニオンバーストは、敵全体のバリアを解除\\n\u3000し、敵全体にキャラの最大ＨＰに応じた物理割\\n\u3000合ダメージを与え、さらにＴＰを小ダウンさせ\\n\u3000る。\\n・最も近い敵を中心にした範囲内の敵すべてに\\n\u3000キャラの最大ＨＰに応じた物理割合ダメージを\\n\u3000与え、さらに物理防御力を大ダウンさせる。\\n\u3000このスキルの効果はダメージを無効化しても付\\n\u3000与される。\\n・敵全体に物理中ダメージを与え、さらに火傷状\\n\u3000態にする。\\n\u3000このスキルにより付与された火傷のダメージで\\n\u3000は、ＴＰが回復しない。\\n・敵全体のバリアを解除し、敵全体に物理中ダ\\n\u3000メージを与え、ノックバックさせる、さらに、\\n\u3000ステータスアップ効果を打ち消す。\\n\u3000このスキルの状態異常と効果はダメージを無効\\n\u3000化しても付与される。\\n・向かって左側のターゲットのブレイク時、自身\\n\u3000の物理防御力と魔法防御力が大ダウン。\\n・中央のターゲットのブレイク時、自身の物理防\\n\u3000御力と魔法防御力が大ダウン。\\n・向かって右側のターゲットのブレイク時、自身\\n\u3000の物理防御力と魔法防御力が大ダウン。\\n・全ターゲットがブレイクされると、自身のＴＰ\\n\u3000を特大ダウン。\\n・ラースドラゴン自身の残りＨＰが30％を切る\\n\u3000とさらに攻撃が激しく強暴になり、ブレイク\\n\u3000されなくなるが、自身の物理防御力と魔法防御\\n\u3000力が大ダウンする。\\n・最も近い敵１キャラに物理大ダメージを与える。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310200, 16, 1, 1000, 0, 0, 'シャークヘッドレイ', 1.6, 310200, '【物理】海蝕洞の支配者として\\n君臨する、サメエイの魔物。凶猛な\\n欲望を全て捕食に注ぐ海の大悪党。\\n\\n・敵全体に物理特大ダメージを与え\\n\u3000物理防御力と魔法攻撃力を\\n\u3000ダウンさせる。\\n\\n・前方範囲内の敵すべてに\\n\u3000魔法小ダメージを与え、ＴＰと\\n\u3000ＴＰ上昇率をダウンさせる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310201, 16, 1, 1000, 0, 0, 'シャークグリード', 1.6, 310201, '【物理】塔の魔力を吸収した、暴食の巨大鮫鱏。\\n音波で感覚を狂わせ、鈍った獲物を喰らい尽くす。\\n\\n・敵全体に物理特大ダメージを与え、物理防御力\\n\u3000と魔法攻撃力をダウンさせる。\\n・最もＴＰの高い敵１キャラに魔法中ダメージを\\n\u3000与え、麻痺させる。\\n\u3000さらに挑発状態にする。\\n・挑発を無視して、最もＨＰの高い敵１キャラに\\n\u3000毎秒増加する継続ダメージを与える。', 0, 270);
