insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310300, 16, 1, 3000, 0, 0, 'アンチビースト', 1.8, 310301, '【物理】愛の力を求め顕現した、負の魔法生物。\\n皆の心の中の愛の力を歪め世界に災いをもたらす。\\n\\n・ユニオンバーストは、異次元より取り出した\\n\u3000アイテムで攻撃を仕掛ける。アイテムによって\\n\u3000効果が変化する。\\n\u3000ハンマーの場合、敵全体にキャラの最大ＨＰに\\n\u3000応じた物理割合ダメージを与え、物理攻撃力を\\n\u3000大幅にダウンさせる。\\n\u3000シンバルの場合、敵全体にキャラの最大ＨＰに\\n\u3000応じた物理割合ダメージを与え、魔法攻撃力を\\n\u3000大幅にダウンさせる。\\n\u3000ボムの場合、敵全体にキャラの最大ＨＰに\\n\u3000応じた物理割合ダメージを与え、\\n\u3000呪い状態にする。\\n\u3000このスキルの状態異常はダメージを無効化して\\n\u3000も付与される。\\n・一番前の敵を中心に、範囲内の敵すべてに\\n\u3000物理中ダメージを与え、さらにノックバック\\n\u3000させる。\\n・一定範囲内の敵すべてに物理大ダメージを\\n\u3000与える。', 0, 270);
