insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310500, 16, 1, 3000, 0, 0, 'ラウドラパーン', 1.6, 310501, '【物理】ポップな体色をした大ウサギの魔物。\\n見た目とは裏腹に迷惑極まりないその歌声は、\\n大地を震わせ冒険者の心をも折る。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与え、\\n\u3000ノックバックさせる。その後、目を\\n\u3000回している間、自身の物理・魔法防御力が\\n\u3000大幅にダウンする。\\n・一番前のキャラを中心として、一定範囲に\\n\u3000物理中ダメージを与え、さらに行動速度を\\n\u3000ダウンさせる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310500, 16, 1, 3000, 0, 0, 'ラウドラパーン', 1.6, 310502, '【物理】ポップな体色をした大ウサギの魔物。\\n見た目とは裏腹に迷惑極まりないその歌声は、\\n大地を震わせ冒険者の心をも折る。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与え、\\n\u3000ノックバックさせる。その後、目を\\n\u3000回している間、自身の物理・魔法防御力が\\n\u3000大幅にダウンする。\\n・一番前のキャラを中心として、一定範囲に\\n\u3000物理中ダメージを与え、さらに行動速度を\\n\u3000ダウンさせる。\\n・前から３キャラに物理小ダメージを与え、\\n\u3000さらに物理防御力を大ダウンさせる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310500, 16, 1, 3000, 0, 0, 'ラウドラパーン', 1.6, 310503, '【物理】ポップな体色をした大ウサギの魔物。\\n見た目とは裏腹に迷惑極まりないその歌声は、\\n大地を震わせ冒険者の心をも折る。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与え、\\n\u3000ノックバックさせる。その後、目を\\n\u3000回している間、自身の物理・魔法防御力が\\n\u3000大幅にダウンする。\\n・一番前のキャラを中心として、一定範囲に\\n\u3000物理中ダメージを与え、さらに行動速度を\\n\u3000ダウンさせる。\\n・前から３キャラに物理小ダメージを与え、\\n\u3000さらに物理防御力を大ダウンさせる。\\n・ラウドラパーン自身のＨＰが40％を切ると、\\n\u3000さらに攻撃が激しく凶暴になる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310500, 16, 1, 3000, 0, 0, 'ラウドラパーン', 1.6, 310504, '【ＭＯＤＥ１】ニュージェネレーションズの\\nシャドウを召喚。森で出会った大ウサギ。\\n３人は夢をあきらめない心を貫き通せるか、\\n大ウサギは各個の弱さを見せつける。\\n\\n・一番前のキャラに物理ダメージを与える。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310500, 16, 1, 3000, 0, 0, 'ラウドラパーン', 1.6, 310505, '【ＭＯＤＥ２】森の人気者の歌声に魔物たちが\\n大挙する。血沸き肉躍る野生の宴会が今、\\n開幕する。\\n\\n・ブルーゴーレム、スリーピィオウル、\\n\u3000ツキツキを倒すと次のＭＯＤＥに移行する。\\n\\n・ラウドラパーンは、一番前のキャラに\\n\u3000物理ダメージを与える。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310500, 16, 1, 3000, 0, 0, 'ラウドラパーン', 1.6, 310506, '【ＭＯＤＥ３】宴も終焉、大兎が最後の力を振り\\n絞る。咆哮にも似た壊震の歌声が、３人の夢道を\\n阻む。\\n\\n・ユニオンバーストは、敵全体にキャラの最大\\n\u3000ＨＰに応じた物理割合ダメージを与え、\\n\u3000ノックバックさせる。その後、目を\\n\u3000回している間、自身の物理・魔法防御力が\\n\u3000大幅にダウンする。\\n・一番前のキャラを中心として、一定範囲に\\n\u3000物理中ダメージを与え、さらに行動速度を\\n\u3000ダウンさせる。\\n・前から３キャラに物理小ダメージを与え、\\n\u3000さらに物理防御力を大ダウンさせる。\\n・バトル開始直後に、さらに攻撃が激しく\\n\u3000凶暴になる。', 0, 270);
insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310507, 16, 1, 3000, 0, 0, 'バニフォルテ', 1.6, 310507, '【物理】世にも奇妙な歌う大ウサギ\\nの魔獣。その歌声はとても褒められ\\nたものではなく、甚だ迷惑である。\\n\\n・ユニオンバーストは、敵全体に\\n\u3000キャラの最大ＨＰに応じた物理割\\n\u3000合ダメージを与え、ノックバック\\n\u3000させる。その後、目を回している\\n\u3000間、自身の物理防御力と魔法防御\\n\u3000力を特大ダウン。\\n\\n・一番前のキャラを中心として、\\n\u3000一定範囲に物理中ダメージを与\\n\u3000え、さらに行動速度を特大ダウン\\n\u3000とスタンさせる。\\n\\n・前から３キャラに物理小ダメージ\\n\u3000を与え、さらに物理防御力を大ダ\\n\u3000ウンさせる。', 0, 270);
