insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (310600, 310600, 16, 1, 3000, 0, 310600, '葛藤のうえきちゃん', 1.6, 310601, '【魔法】魔物かはたまた精霊なのか、謎多き巨躯\\nのいきもの。如何ともし難いその表情のまま謎の\\n花粉を噴霧し、市中を混乱へと誘い落とす。\\n\\n・ユニオンバーストは、敵全体に魔法大ダメージ\\n\u3000を与え、物理防御力と魔法防御力をダウンさせ\\n\u3000る。\\n・前方範囲内の敵に物理ダメージを与え、ノック\\n\u3000バックさせる。\\n・左右の葉っぱは、ブレイクすると一定時間消滅\\n\u3000する。\\n・向かって左側の葉っぱのブレイク時、物理防御\\n\u3000力と魔法防御力が小ダウン。\\n・本体のブレイク時、物理防御力と魔法防御力が\\n\u3000大ダウン。\\n・向かって右側の葉っぱのブレイク時、物理防御\\n\u3000力と魔法防御力が小ダウン。', 0, 270);
