insert into v1_83e7b4bb74df9c448153a3d87f5bcbc5302e0de80b3048aa5a22699d4422b9bf values (0, 310800, 16, 1, 3000, 0, 0, '忍びの頭領ハンゾウ', 2.8, 310800, '【物理】悪徳監査官に雇われた忍びの頭領。\\n義を失った忍びは、その身を金に落とす。だが\\nしかし、円熟の業は健在で皆の行く手を阻む。\\n\\n・バトル開始後、監査官が登場する。監査官がダ\\n\u3000メージを受けると、その直後に忍びの頭領ハン\\n\u3000ゾウの物理防御力と魔法防御力を特大ダウンさ\\n\u3000せる。\\n・ユニオンバーストは、敵全体にキャラの最大Ｈ\\n\u3000Ｐに応じた物理割合ダメージを与える。\\n・大ガマに変化して毒を吐き、敵全体を毒状態に\\n\u3000する。この攻撃は必ず命中する。\\n・敵全体に物理中ダメージを与える。', 0, 270);
